const float NUM_ITERATIONS = 512.0;
const float zoomSpeed = 10.0;
const float width = 6.5;
//const vec2 center = vec2(-0.742978, 0.1);
//const vec2 center = vec2(-0.747005, 0.150006);
const vec2 center = vec2(0.0, 0.0);


vec2 C = vec2(0.0, 1.0);

vec2 f(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2.0 * z.y * z.x) + C;
}

float iter(vec2 z) {

    // float nrm = dot(c, c);
    // http://iquilezles.org/www/articles/mset_1bulb/mset1bulb.htm
    // if( 256.0*nrm*nrm - 96.0*nrm + 32.0*c.x - 3.0 < 0.0 ) return 0.0;
    // http://iquilezles.org/www/articles/mset_2bulb/mset2bulb.htm
    //if( 16.0*(nrm+2.0*c.x+1.0) - 1.0 < 0.0 ) return 0.0;

    float i;
    for (i = 0.0; i < NUM_ITERATIONS; i += 1.0) {
        z = f(z);
        if (dot(z, z) > 4.0) break;
    }
    if (i >= NUM_ITERATIONS) 
        return 0.0;  

    // smooth out the coloring
    float si = i - log2(log2(dot(z,z))) + 4.0;
    float ai = smoothstep( -0.1, 0.0, 0.0);
    i = mix(i, si, ai);

    return i;
}

vec2 rotateCoord(vec2 c) {
    float theta = 3.14 + 3.14 * cos(iTime / 10.0);
    float x = (c.x - center.x) * cos(theta) - (c.y - center.y) * sin(theta) + center.x;
    float y = (c.x - center.x) * sin(theta) + (c.y - center.y) * cos(theta) + center.y;
    return vec2(x, y);

}

vec2 transformCoord(vec2 fragCoord) {
    //float rescale = pow(zoomSpeed, -2.75 + 2.75*cos(iTime / 10.0));
    float rescale = 2.0 - cos(iTime/10.0);
    vec2 size = vec2(width, iResolution.y / iResolution.x * width) * rescale;
    vec2 uv = fragCoord/iResolution.xy;
    return rotateCoord((uv - 0.5) * size + center);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    C.x = 2.0*sin(iTime/20.0);
    C.y = cos(1.3) + 0.25*cos(iTime);
    vec2 c = transformCoord(fragCoord);
    float i = iter(c);    
    // color calculation
    vec3 col = 0.5 + 0.5*cos(3.0 + 0.25*i + vec3(0.50, cos(iTime/5.0)/2.0 + 0.5, 0.05));
    fragColor = vec4(col, 1.0);
}

#define PI 3.1415926535897932384626433832795
//#define SINGLE_SHOT

const vec4 color1 = vec4(136.0, 199.0, 220.0, 255.0) / 255.;
const vec4 color2 = vec4(199.0, 220.0, 136.0, 255.0) / 255.;
const vec4 color3 = vec4(220.0, 136.0, 199.0, 255.0) / 255.;
const vec4 color4 = vec4(199.0, 136.0, 220.0, 255.0) / 255.;
const vec4 color5 = vec4(220.0, 199.0, 136.0, 255.0) / 255.;
const vec4 color6 = vec4(136.0, 220.0, 199.0, 255.0) / 255.;
const vec4 color7 = vec4(236.0, 120.0, 109.0, 255.0) / 255.;
const vec4 color8 = vec4(120.0, 109.0, 236.0, 255.0) / 255.;


const float delta = 0.8;
const float e = exp(1.0);

struct ColorFun
{
    vec4 color;
    vec2 fun;
};

// Some trial functions to generate the checkerboard.

vec2 squareWave(vec2 x)
{
    return (1.0/atan(1.0/delta))*atan(sin(x)/delta);
}

// This one seems the most pleasing
vec2 doubleSine(vec2 x)
{
    return sin(sin(x)) / sin(1.0);
}

vec2 polynomialWave(vec2 x, float powerOverTwo)
{
    vec2 z = fract(abs(x / PI));
    vec2 periodize = sign(fract(abs(x) / (2.0 * PI)) - 0.5) * sign(x);
    vec2 zPow = pow((z - 0.5) * (z - 0.5), vec2(powerOverTwo));
    vec2 singleWave = pow(2.0, powerOverTwo * 2.0) * zPow - 1.0;
    return singleWave * periodize;
}

vec2 expWave(vec2 x)
{
    return 1.0 - 2.0*(exp(sin(x) - 1.0 / e)) / (e - 1.0 / e);
}

vec2 expWave2(vec2 x)
{
    return 1.0 - 2.0*(exp(sin(sin(x)) / sin(1.0) - 1.0 / e)) / (e - 1.0 / e);
}

ColorFun resolveSection(vec2 uv)
{
    vec2 arg = uv * 128.0;
    int num = int(fract(uv.x) * 8.0);
    vec2 adjArg = vec2(2.0 * arg.x - 0.5 * arg.y, -0.5 * arg.x - 2.0 * arg.y);
    vec2 val = vec2(0.0);
    vec4 color;
    vec2 fun;
    switch(num)
    {
        case 0: return ColorFun(color1, sin(adjArg));
        case 1: return ColorFun(color2, doubleSine(adjArg));
        case 2: return ColorFun(color3, squareWave(adjArg));
        case 3: return ColorFun(color4, polynomialWave(adjArg, 1.0));
        case 4: return ColorFun(color5, polynomialWave(adjArg, 2.0));
        case 5: return ColorFun(color6, polynomialWave(adjArg, 3.0));
        case 6: return ColorFun(color7, expWave(adjArg));
        case 7: return ColorFun(color8, expWave2(adjArg));
    }
}


float createBandMask(float x, float blurRadius, float width, float base)
{
    width -= 2. * blurRadius;
    return smoothstep(0.0, blurRadius, abs(x - base - width / 2. - blurRadius) - width / 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float adjTime = iTime + 0.3;
#ifdef SINGLE_SHOT
    adjTime = min(5.0, adjTime);
#endif

    vec2 uv = fragCoord / iResolution.x;

    float streakAngle = (-1.0 / 3.0 * uv.x - 3.0 * uv.y) / 6.0; // As with the checkerboard, we want the wave at an angle, but a different one.
    float animProgress = streakAngle + 0.10 * adjTime; // Let's animate it
    animProgress = fract(animProgress);
    ColorFun cf = resolveSection(uv);
    // Get the checkerboard.
    float mask = (cf.fun.x + cf.fun.y) / 4.0 - 0.5;
    // Now, we will offset the checkerboard with a smoothed line.
    // This will create the effect of the checkerboard dots appearing
    // On the wavefront.
    // Scaling was used so that when there is no wave, the whole checkerboard is < 0, and behind the front it's > 1
    float shiftVal = 2.0 * (1.0 - createBandMask(animProgress, 0.21, 0.75, 0.0));
    float alpha = mask + shiftVal;
    // https://github.com/glslify/glsl-aastep
    float aawidth = length(vec2(dFdx(alpha), dFdy(alpha))) / sqrt(2.0);
    alpha = smoothstep(0.5 - aawidth, 0.5 + aawidth, alpha);
    float result = clamp(alpha, 0.0, 1.0);
    fragColor = cf.color * result + cf.color * (1.0 - result) * 0.5;
}

