#define c2 0.

#define c1 vec4(3.0+c2,2.5+c2,1.5,0)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 6.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;

    float offset = .5;
    float scale2 = 1.5;
    for(int c=0;c<3;c++){
        float bend = 1.;
        float scale = c1.z;
        for(int i=0;i<3;i++)
        {

            for(int k = 0; k < 9; k++){       
                uv /= -scale2;
                vec2 s1 = triangle_wave(uv.yx-offset,scale)*bend;
                vec2 s2 = triangle_wave(uv+offset/scale2,scale)/bend;
                uv = s1+s2;
                //bend = 1.+(uv.x)/3.;
            }
            scale /= 1.+(scale2)*col.x/(8.);
            scale2 -= (col.x-1.)/4.;
            col[c] = abs(col[c] - ((uv.x)-(uv.y)));
            //col[c] = abs(col[c] + ((uv.x)+(uv.y)))/4.;

        }
	}
    fragColor = vec4(vec3(col),1.0);
}

float thingy(vec3 var){
  vec3 b=vec3(0.9);
  vec3 q=var-b;
  return length(max(q,4.0))+min(max(q.x,max(q.y,q.z)),0.0);
}

float displacement(vec3 var){
	float height = ((sin(6.0*var.y)*cos(10.0*var.x)*tan(6.0*var.z))*cos(60.1));
	return height;
}

mat2 rotation(float theta){
	float sinx=sin(theta);
	float cosx=cos(theta);
	return mat2(cosx,-sinx,sinx,cosx);
}

float mainShapeSphere(vec3 var){
	return length(var)-(cos(iTime)+.7);
}



vec3 getNormal(vec3 var){
	return normalize(var);
}




float dist(vec3 var){
	var.xy*=rotation(iTime);
	var.xz*=rotation(iTime);
	float x=displacement(var);
	float mysphere=mainShapeSphere(var);
	float box=thingy(var);
	return x+mysphere;
}




void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 mouse = vec2(iMouse)/vec2(iResolution);
	vec3 look=vec3(1.0,0.0,0.0);



	vec2 uv=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;


	vec3 ro=vec3(0.0,0.0,-5.0);
	float zoom= 3.0;

	vec3 focus=normalize(look-ro);
	vec3 ray=normalize(cross(vec3(3.0,1.0,0.0),focus));

	vec3 u=cross(focus,ray);
	vec3 c=ro+focus*zoom;
	vec3 i=c+uv.x*ray+uv.y*u;
	vec3 rd=normalize(i-ro);
    vec3 k,l;


    float d,t;
	vec3 color;

    for(int i=0;i<40;i++){
        vec3 k=ro+rd*t;

        d=dist(k);
        l=getNormal(k);
        if(d<.02)break;
        t+=d;
    }

	color=reflect(cos(l),l);

	fragColor=vec4(color,6.0);
}

float PI = 3.14159265;

mat3 rotMatrix(vec3 rotation){
    rotation *= PI/180.;
    float rx = rotation.x;
    float ry = rotation.y;
    float rz = rotation.z;

    mat3 rotationX = mat3( 1      , 0      , 0      ,
                           0      , cos(rx),-sin(rx),
                           0      , sin(rx), cos(rx));

    mat3 rotationY = mat3( cos(ry), 0      , sin(ry),
                          0       , 1      , 0      ,
                          -sin(ry), 0      , cos(ry));

    mat3 rotationZ = mat3( cos(rz),-sin(rz), 0      ,
                           sin(rz), cos(rz), 0      ,
                           0      , 0      , 1);

    return rotationX * rotationY * rotationZ;
}


float sphere(vec3 position, vec3 center, float size){
    return length(position-center)-size;
}


float metaball(vec3 position, vec3 center, vec3 rotation, vec3 scale){
    float size = 2.;
    float l = length(rotMatrix(rotation)*(position-center)/(scale));
    return -1./l/l/l/l*size;
}

