float head(vec3 position){
    return 
            metaball(position, vec3(0.,0.,0.), vec3(0,0,0), vec3(0.671,1,1))
          + metaball(position, vec3(0.,-0.5,-0.9), vec3(0,0,0), vec3(0.400,0.472,0.538))
          // jaw
          + metaball(position, vec3(0.3,-0.3,0.1), vec3(0,0,0), vec3(0.450,0.472,0.538))
          + metaball(position, vec3(-0.3,-0.3,0.1), vec3(0,0,0), vec3(0.450,0.472,0.538))
