          + metaball(position, vec3(1.,0.71,0.47), vec3(0,60,-50), vec3(0.2,0.75,0.415))
          + metaball(position, vec3(-1.,0.71,0.47), vec3(0,-60,50), vec3(0.2,0.75,0.415))
