          - metaball(position, vec3(-1.2,0.8,0.32), vec3(0,-60,50), vec3(0.07,0.40,0.255))/1.5
          // neck
          + metaball(position, vec3(0,-1.729,0.15), vec3(-1.6,0,0), vec3(0.363,0.683,0.435))
          + metaball(position, vec3(1.04,-2.64,0.383), vec3(0,0,0), vec3(0.671,0.671,0.671))
          + metaball(position, vec3(-1.04,-2.64,0.383), vec3(0,0,0), vec3(0.671,0.671,0.671))
          + metaball(position, vec3(0,-2.729,0.15), vec3(0,0,0), vec3(0.6,0.6,0.6))
          // left brow
          + metaball(position, vec3(0.9-0.1,0.45,-0.33), vec3(35.6,-7.,0), vec3(0.213,0.162,0.269))
          + metaball(position, vec3(0.647-0.1,0.49,-0.55), vec3(20, 0, 0), vec3(0.213,0.184,0.269))
          + metaball(position, vec3(0.86-0.1,0.02,-0.41), vec3(0, 0, 0), vec3(0.213,0.158,0.269))
          - metaball(position, vec3(0.66-0.1,0.13,-1.21), vec3(0, 0, 0), vec3(0.15,0.15,0.3))
          // right brow
          + metaball(position, vec3(-0.9+0.1,0.45,-0.33), vec3(35.6,7.,0), vec3(0.213,0.162,0.269))
          + metaball(position, vec3(-0.647+0.1,0.49,-0.55), vec3(-20, 0, 0), vec3(0.213,0.184,0.269))
          + metaball(position, vec3(-0.86+0.1,0.02,-0.41), vec3(0, 0, 0), vec3(0.213,0.158,0.269))
          - metaball(position, vec3(-0.66+0.1,0.13,-1.21), vec3(0, 0, 0), vec3(0.15,0.15,0.3))
          // end
          + 1.
            ;
}

