float sdfIntersect(out bool intersect, out vec3 position, out vec3 normal, in vec3 rayOrigin, in vec3 rayDir){
    float advStep = .1;
    int dichoSteps = 10;

    vec3 rayPos = rayOrigin;

    float sdfValue = 10.;//sdf(rayPos);
    float newSdfValue = sdf(rayPos);
    int nbSteps = 0;
    vec3 a = rayPos;
    vec3 b = rayPos;
    while (newSdfValue >= 0.){
        //rayPos += rayDir*advStep;  //constant step
        rayPos += (newSdfValue/3.4+0.01)*rayDir;
        a = b;
        b = rayPos;
        /*
        if (newSdfValue < sdfValue) {
            rayPos += (newSdfValue/3.+0.01)*rayDir;
        } else {
            rayPos += (0.01)*rayDir;
        }*/

        sdfValue = newSdfValue;
        newSdfValue = sdf(rayPos);

        /*
        if (newSdfValue > sdfValue){
            intersect = false;
            return;
        }*/

        if (nbSteps > 100 || sdfValue > .998){
            intersect = false;
            return 0.;
        }

        nbSteps += 1;
    }

    // dichotomy
    //vec3 a = rayPos - rayDir*advStep;
    //vec3 b = rayPos;
    vec3 ab = vec3(0,0,0);
    float sdfA;
    float sdfB;
    float sdfAB;
    for (int i; i<dichoSteps; i++){
        sdfA = sdf(a);
        sdfB = sdf(b);

        ab = (a+b)*0.5;
        sdfAB = sdf(ab);

        if (sdfAB < 0.){
            b = ab;
        } else {
            a = ab;
        }
    }
    sdfValue = sdfAB;

    intersect = true;
    position = ab;

    float epsilon = 0.0001;
    vec3 preNormal = vec3(sdf(position + epsilon*vec3(1,0,0)) - sdfValue,
                          sdf(position + epsilon*vec3(0,1,0)) - sdfValue,
                          sdf(position + epsilon*vec3(0,0,1)) - sdfValue);

    normal = normalize(preNormal);

    return length(preNormal);
}


vec3 light(in vec3 lightsource, in vec3 color, in vec3 position, in vec3 normal){
    bool intersect = false;
    vec3 position2 = vec3(0,0,0);
    vec3 normal2 = vec3(0,0,0);

    vec3 rayDir = normalize(lightsource - position); 
    vec3 rayOrigin = position + 0.1*rayDir;

    sdfIntersect(intersect, position2, normal2, rayOrigin, rayDir);

    if (intersect){
        return vec3(0,0,0);
    } else {
        return color * dot(normal, rayDir);
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5, 0.5);

    vec2 sensorDims = vec2(16, 9);
    float focal = 10.;

    bool intersect = false;
    vec3 position = vec3(0,0,0);
    vec3 normal = vec3(0,0,0);

    float time = iTime;

    vec3 rayOrigin = vec3(5.*sin(time),0,-5.*cos(time));
    vec3 rayDir = normalize(vec3(uv.x*sensorDims.x, uv.y*sensorDims.y, focal));

    mat3 rotation = mat3( cos(time), 0       , sin(time),
                          0        , 1       , 0        ,
                          -sin(time), 0       , cos(time));

    rayDir = rotation * rayDir;

    float pointiness = sdfIntersect(intersect, position, normal, rayOrigin, rayDir);

    vec3 col = vec3(1,1,1);
    if (intersect){
        //col = 0.5*normal + vec3(0.5, 0.5, 0.5);//vec3(1,1,1);   //vec3(0.4, 0.4, 0.8)
        col = light(vec3(5,5,-4), vec3(1,1,1), position, normal) + light(vec3(-3,1,1), vec3(0.4, 0.4, 0.8), position, normal);
        if (pointiness*1000. > 0.15){
            col *= vec3(1,.8,.8)*(1.-min(0.6,max(0., pointiness*1000.)));
        }
    } else {
        col.x = (uv.y+.5)/4.;
        col.y = (uv.y+.5)/4.;
        col.z = .1+(uv.y+.5)/4.;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 invertPoint(vec2 p) {
    vec2 p2 = vec2(p.y, -p.x);
    p2 /= dot(p, p);
    return p2;
}

float sdfCircles(vec2 p) {
    // Start with the infinite line
    float d = abs(p.y - 1.0);
    // How about another infinite line, and we only draw circles inbetween?
    d = min(d, abs(p.y - 6.0));

    if((p.y > 1.0) && (p.y < 6.0)) {
        // Then draw all of the grid circles
        p = fract(p);
        d = min(d, abs(0.5 - length(p - 0.5)));
    }
    return d;
}

const float PI = 3.141592653589793;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    if(iMouse.xy == vec2(0.0))
        mouse = vec2(0.0);

    float anim = 0.5-0.5*cos(PI * iTime / 12.0);
    float zoom = 0.1 * exp(4.0*anim);
    vec2 offset = mix(vec2(0.0, 2.0), vec2(-0.001, 0.0), pow(anim, 0.3));

    offset -= mouse / zoom;

    vec2 p = uv / zoom + offset;
    vec2 p2 = invertPoint(p);

    vec2 tmp = 2.0*abs(fract(p) - 0.5);
    float grid = 1.0 - max(tmp.x, tmp.y);
    grid *= 8.0 * zoom;

    float d1 = sdfCircles(p);
    float d2 = sdfCircles(p2);

    // Time varying pixel color
    vec3 col = vec3(0.0);
    col += smoothstep(0.05, 0.03, grid) * vec3(0.3);
    col += smoothstep(0.03, 0.01, d1) * vec3(0.9, 0.2, 0.1);
    col += smoothstep(0.03, 0.01, d2) * vec3(abs(p2.xy), 0.9);

    // Output to screen
    fragColor = vec4(col,1.0);
}

