float grid(vec3 p) {


  vec3 p2 = p;
  p2.y = (fract(p.y)-0.5);
  float d = box(p2, vec3(10.0,0.2,0.05));

  p.x = (fract(p.x)-0.5);

  d = min(d, box(p, vec3(0.2,5,0.05)));

  return d;
}

