vec3 lattice (float t ) {



  float m = t;
  float mx = floor((m-2.)/3.);
  float my = floor((m-1.)/3.);
  float mz = floor((m-0.)/3.);

  float n= m+1.;
  float nx = floor((n-2.)/3.);
  float ny = floor((n-1.)/3.);
  float nz = floor((n-0.)/3.);





  vec3 a =  
  mix(
      vec3(
          rand(mx)-.5,
          rand(my)-.5,
          rand(mz)-.5
      ),
      vec3(
          rand(nx)-.5,
          rand(ny)-.5,
          rand(nz)-.5
      ) , 
      fract(t)
  );
  return a;
}

float map(vec3 p) {

  // geo

  float outside = cyl(p.xz, 3.);
  float inside = -cyl(p.xz, 2.8);
  float tunnel = max(inside,outside);
  p.xz *= rot((p.y/10.) * sin(iTime) * 5.1);
  p = rep(p, vec3(.52));
  float holes = sph(p, .27);

  tunnel = max(tunnel,-holes);
  return tunnel;
}

float rnd(vec2 uv) {
  return fract(dot(sin(uv*754.655+uv.yx*942.742),vec2(3847.554)));
}

float rnd(float t) {
  return fract(sin(t*472.355)*655.644);
}

float curve(float t) {
  return mix(rnd(floor(t)), rnd(floor(t)+1.0), smoothstep(0.0,1.0,fract(t)));  
}




float shadow(vec3 p, vec3 l, float maxdist, int stepcount, float limit) {
  float shad=1.0;
  float dd=0.0;
  for(int i=0; i<stepcount; ++i) {
    float d=min(map(p), maxdist);
    if(d<limit) {
      shad=0.0;
      break;
    }
    if(dd>=maxdist) {
      break;
    }

    p += l*d;
    dd+= d;

  }
  return shad;
}



