  vec3 s,t;
  s += lattice(tt) * 20.;
  s += lattice(tt * 3.) * 5.1;
  s *= sin(tick(iTime * .31) * .21+ 7.);

  t += lattice(tt * 95. + 1.) * 3.;
  t += lattice(tt * 57.) * 1.1;;




  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,-1,0)));
  vec3 cy=normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz*0.7);





  // march

  vec3 p=s;
  float dd=0.0;
  const float maxdist = 100.0;
  float z;
  for( z=0.; z<100.; ++z) {

    float d=map(p);
    if(d<0.001) break;
    if(dd>maxdist) { dd=maxdist; break;}
    p+=r*d;
    dd+=d;

  }



  // volume

  float rand=rnd(uv);

