// the weighting of each sample point is 1 / distance^BLEND_FALLOFF_POWER
const float BLEND_FALLOFF_POWER = 4.0;


// sample point are contained in a square of this 'radius'.
const float sweepRadius = 10.0;

// distance between sample points.
const float sweepStep   = sweepRadius / 5.0;

// every third and seventh sample points are removed for some irregularity.




#define RES iResolution


mat2 rotationMatrix2(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, s, -s, c);
}

const float sinData[] = float[](
    // angle
    // frequency
    // phase
    // amplitude
    2.5, 0.11, 1.5, 3.0,
    3.1, 0.71, 1.5, 2.0,
    6.1, 1.31, 1.5, 1.0,
    2.2, 0.41, 1.5, 4.0
);
float groundTruth(in vec2 p) {

    float accum = 0.0;
    float range = 0.0;
    for (int n = 0; n < sinData.length(); n += 4) {
        vec2  v   = p * rotationMatrix2(sinData[n + 0]);
        float val = sin(v.y * sinData[n + 1] + sinData[n + 2]) * sinData[n + 3];
        accum += val;
        range += sinData[n + 3];
    }

    float f = accum * 0.5 / range + 0.5;

    return f;
}

void accumulateSample(in vec2 p, in vec2 samplePt, inout float accumVal, inout float accumRng) {

    float sampleVal = groundTruth(samplePt);
    float sampleDst = length(p - samplePt);

    float weight = 1.0 / pow(sampleDst, BLEND_FALLOFF_POWER);
    accumVal += sampleVal * weight;
    accumRng += weight;
}

bool discardSamplePoint(int n) {
    return n % 3 == 0 || n % 7 == 0;
}


float interpolated(in vec2 p, in vec2 screenCenter, in vec2 mousePt) {
    float accumVal = 0.0;
    float accumRng = 0.0;

    accumulateSample(p, screenCenter + mousePt       , accumVal, accumRng);


    int n = 0;
    for (float dx = -sweepRadius; dx <= sweepRadius; dx += sweepStep) {
    for (float dy = -sweepRadius; dy <= sweepRadius; dy += sweepStep) {
        ++n;
        if (discardSamplePoint(n)) {
            continue;
        }
        accumulateSample(p, screenCenter + vec2(dx, dy), accumVal, accumRng);
    }
    }

    return accumVal / accumRng;
}

// doing fwidth 'yourself' with dFdx() and dFdy()
// yields finer results on some systems, iirc.
float fwidth_home(float val) {
    return abs(dFdy(val)) + abs(dFdx(val));
}

// val in [0, 1]
float isolines(float val) {
    val = (val - 0.5) * 1.01 + 0.5;
    return smoothstep(2.0, 0.0, abs(mod(val + 0.05, 0.1) - 0.05) / fwidth_home(val));
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
    // get normalized xy screen-coordinates in 
    float zoomFactor       = 1.2;
    float smallDim         = min(RES.x, RES.y);
    vec2  xy               = (XY - RES.xy * 0.5) /smallDim * 2.0 * zoomFactor;
    bool  isLeftSide       = xy.x < 0.0;

    // for smooth lines
    float edgeWidth        = 2.0;
    float smoothingEpsilon = edgeWidth / smallDim * 2.0;

    // world-space coord
    vec2  worldOffset      = vec2(cos(iTime * 0.0051), sin(iTime * 0.0041)) * 100.0;
    vec2  p                = xy * 10.0 + worldOffset;
    vec2  resetSide        = vec2(RES.x / smallDim * zoomFactor * -10.0, 0.0);

    vec2  mouse            = (iMouse.xy - RES.xy * 0.5) / smallDim * 2.0 * zoomFactor;
    vec2  samplePt         = mouse * 10.0 - resetSide * 0.5;

    float val;
    if (isLeftSide) {
        val = groundTruth(p);
    }
    else {
        val  = interpolated(p + resetSide, worldOffset + resetSide * 0.5, samplePt);
    }

    vec3 rgb  = val * vec3(0.5, 0.8, 1.0);

    float iso = isolines(val);
    rgb       = mix(rgb, vec3(0.5, 0.5, 0.3), iso);

    // draw sample points
    float ptRad = 0.2;
    vec3  ptCol = vec3(1.0);
    float dp;
    if (isLeftSide) {   
        dp = length(p - (worldOffset + resetSide * 0.5 + samplePt));
        rgb = mix(rgb, ptCol, smoothstep(smoothingEpsilon * 12.0, 0.0, abs(dp - ptRad) - 0.01));
    }
    else {
        dp = length(p + resetSide - (worldOffset + resetSide * 0.5 + samplePt));
        rgb = mix(rgb, ptCol, smoothstep(smoothingEpsilon * 12.0, 0.0, abs(dp - ptRad) - 0.01));
    }

    int n = 0;
    for (float dx = -sweepRadius; dx <= sweepRadius; dx += sweepStep) {
    for (float dy = -sweepRadius; dy <= sweepRadius; dy += sweepStep) {
        ++n;
        if (discardSamplePoint(n)) {
            continue;
        }
        if (isLeftSide) {
            dp = length(p - (worldOffset + resetSide * 0.5 + vec2(dx, dy)));
            rgb = mix(rgb, ptCol, smoothstep(smoothingEpsilon * 12.0, 0.0, abs(dp - ptRad) - 0.01));
        }
        else {
            dp = length(p + resetSide - (worldOffset + resetSide * 0.5 + vec2(dx, dy)));
            rgb = mix(rgb, ptCol, smoothstep(smoothingEpsilon * 12.0, 0.0, abs(dp - ptRad) - 0.01));
        }
    }
    }

    // center divider
    rgb       = mix(rgb, vec3(0.0, 0.0, 0.0), smoothstep(smoothingEpsilon * 2.0, 0.0, abs(xy.x)));


    RGBA.rgb = rgb;
    RGBA.a   = 1.0;
}

#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define R(p,a,t)mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define Y p=p.x<p.y?p.zxy:p.zyx
#define N p=p.x>p.y?p.zxy:p.zyx

#define hash(p)fract(sin(p*12345.5))

vec3 randVec(float s)
{
    vec2 n=hash(vec2(s,s+23715.3));
    return vec3(cos(n.y)*cos(n.x),sin(n.y),cos(n.y)*sin(n.x));
}

vec3 randCurve(float t)
{
    t*=.5;
    float n = 1234.567;
    vec3 p = vec3(0);
    for (int i=0; i<3; i++)    {
        p += randVec(n+=365.)*sin((t*=1.3)+sin(t*0.6)*0.5);
    }
    return p*.8;
}

mat3 lookat(vec3 eye, vec3 ta)
{
    vec3 w=normalize(ta-eye), u=normalize(cross(w,vec3(0,1,0)));
    return mat3(u,cross(u,w),w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 p,ta,eye,
    r=iResolution,
    d=normalize(vec3((fragCoord-.5*r.xy)/r.y,1.)),
    c=vec3(0);
    float i=0.,g=0.,e,a,s,t=iTime*.5;  
    for(;i++<120.;){
        eye= randCurve(t-.3);
        ta= randCurve(t+.3);
        p=lookat(eye,ta)*g*d+eye;
        a=dot(p,p);
        p=abs(p)-.6;
        N;
        Y;
        s=3.;
        for(int i=0;i++<8;){
            p=abs(p);
            Y;
            s*=e=1.8/min(dot(p,p),1.3);
            p=abs(p)*e-vec3(1.5*a,3.,5.*a);
        }
        a=.3;
        p-=clamp(p,-a,a);
        g+=e=length(p.yz)/s;
        if(e<.001)c+=mix(vec3(1),H(log(s)*.2+.2),.5)*.015*exp(-i*i*e);
    }
    c*=c*c;
    fragColor=vec4(c,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*acos(cos(atan(iTime/1.1))))/iResolution.xy*acos(cos(atan(iTime/1.1)));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*sin(cos(tan(iTime*0.01+uv.xyx+vec3(0.1,0.2,0.4))));

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926;
const float vfov=pi/2.0;
const float dt = 0.04;
const float R2 = pow(16., 2.);
const vec3 start = vec3(12., 0., 0.);
const vec3 lookat =  vec3(2., 0., 0.);

vec3 disk_color(vec3 p) {
    float r = length(p);

    vec2 ra = vec2(atan(abs(p.z), p.x), r/3.);
    vec3 col = (texture(iChannel0, ra).xyz + vec3(0.5))/(r-4.6)*23.;
    return col;
}

float sdTorus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

vec3 bg_color(vec3 p) {
    p = normalize(p);
    float phi = atan(p.y, p.x);
    float theta = acos(p.z);
    float x = phi/2.0/pi;
    float y = theta/pi;
    vec2 texture_p = vec2(x, y)*4.;

    return pow(texture(iChannel0, texture_p).yzx, vec3(3.6));
}

vec3 integrate(vec3 p, vec3 v) {
    vec3 c = cross(p, v), col=vec3(0.);
    float h2 = dot(c, c), p2 = 0., dis=0.;


    for (int i=0; i<80; i++) {
        p2 = dot(p, p);
        if (0. < p2) {
            vec3 a = -1.5*h2*p/pow(p2, 2.5);
            float ra = length(a);
            float step_size = dt/(0.13+pow(ra, 2.1));
            v += a*step_size;
            vec3 new_p = p + v*step_size;
            dis += length(v*step_size);


            if (p.y*new_p.y<0.) {
                vec3 hit = mix(p, new_p, abs(p.y/(new_p.y-p.y)));
                float r = length(hit);
                if (4.8 < r && r< 6.4) {
                    col += disk_color(hit) / pow(dis, 1.6);
                }
            }
            p = new_p;
            //if (sdTorus(p, vec2(5.6, 0.8)) < 0.) col += vec3(0.08);
            col += vec3(0.85,0.8,0.95) * smoothstep(0., 0.26, length(p)-2.)*(0.18/p2);
        } else {
            break;
        }
    }
    if (p2>R2) {
        col += bg_color(p);

    }
    return col;
}

mat2 rotate(float a) {
    return mat2(
        cos(a), -sin(a),
        sin(a), cos(a)
    );
}

vec3 ray_direction(vec3 ro, vec3 lookat, vec3 up, vec2 uv) {
    vec3 r = normalize(lookat-ro);
    up = normalize(up-dot(up, r)*r);
    vec3 x = cross(r, up)*uv.x;
    vec3 z = iResolution.y/tan(0.5*vfov)*r;
    vec3 y = up*uv.y;
    return normalize(x+y+z);
}

vec3 get_color(vec3 ro, vec3 lookat, vec3 up, vec2 uv) {
    vec3 rd = ray_direction(ro, lookat, up, uv);
    return integrate(ro, rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime *0.2;
    vec3 ro; ro.xz = rotate(t)*start.xz;ro.y+=2.;
    vec3 ro1; ro1.xz = rotate(t+0.02)*start.xz; ro1.y=ro.y+0.8;


    vec2 uv = fragCoord-iResolution.xy*0.55;

    vec3 up = normalize(ro1-ro);

    vec3 col = get_color(ro, lookat, up, uv);

    fragColor = vec4(col, 1.0);
}

