  DMINQ (idSup);
  q.y -= 2. * h;
  d = PrCylDf (vec3 (q - vec3 (0., - h, 0.)).xzy, 0.06, h);
  DMINQ (idTube);
  q.xz = Rot2D (q.xz, 0.1 * pi * a * sx);
  d = PrRoundBoxDf (q, vec3 (0.05, 0.05, 0.16), 0.1);
  DMINQ (idSeat);
  d = PrSphDf (vec3 (q.xy, abs (q.z) - 0.24), 0.05);
  DMINQ (idLitS);
  q.yz = Rot2D (q.yz - vec2 (0.2, -0.2), 0.07 * pi);
  d = min (PrCylDf (q.xzy, 0.04, 0.3), PrCylDf ((q - vec3 (0., 0.3, 0.)).yzx, 0.03, 0.25));
  DMINQ (idGrip);
  return dMin;
}

float GObjRay (vec3 ro, vec3 rd)
{  // (raymarching with axial grid reduces scene complexity when symmetry absent)
  vec3 p;
  vec2 cs;
  float dHit, d, eps, rgOut;
  rgOut = 10.;
  eps = 0.001;
  cs = sin (pi / nSec + vec2 (0., 0.5 * pi));
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    p.xz = Rot2D (p.xz, aRot);
    gAng = (floor (nSec * atan (p.z, - p.x) / (2. * pi)) + 0.5) / nSec;
    d = GObjDf (p);
    p.xz = Rot2D (p.xz, 2. * pi * gAng);
    d = min (d, 0.1 + max (- p.x - rgOut, abs (dot (vec2 (p.x, abs (p.z)), cs))));
    dHit += d;
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 GObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a, s;
  dMin = dstFar;
  q = p;
  q.y -= 0.05;
  d = PrCylDf (q.xzy, 9.3, 0.05);
  DMINQ (idBase);
  d = PrTorusDf (vec3 (q.xz, abs (abs (q.y - 1.) - 0.3) - 0.3), 0.03, 9.);
  q.xz = Rot2D (q.xz, 2. * pi * floor (32. * atan (q.z, - q.x) / (2. * pi) + 0.5) / 32.);
  q.xy -= vec2 (-9., 0.8);
  d = min (d, PrCylDf (q.xzy, 0.03, 0.8));
  DMINQ (idRail);
  p.xz = Rot2D (p.xz, aRot);
  q = p;
  q.y -= 0.3;
  d = PrCylDf (q.xzy, 1., 0.5);
  DMINQ (idCmid);
  q.y -= 0.6;
  d = PrSphDf (q, 0.12);
  DMINQ (idLit);
  a = (floor (0.5 * nSec * atan (p.z, - p.x) / (2. * pi)) + 0.5) / (0.5 * nSec);
  q = p;
  q.y -= 0.1;
  q.xz = Rot2D (q.xz, 2. * pi * a);
  d = PrCaps2Df (vec2 (q.y - 0.1, q.x + 5.), 0.06, 2.5);
  DMINQ (idFlr);
  d = PrCylDf ((q - vec3 (-1.25, 0.1, 0.)).yzx, 0.07, 1.25);
  DMINQ (idSpk);
  d = Maxv2 (abs (Rot2D (q.xy, -0.05 * pi) - vec2 (-3.6, 5.2)) - vec2 (3.2, 0.04));
  d = min (d, min (Maxv2 (abs (vec2 (q.x + 7.3, q.y - 3.95)) - vec2 (0.02, 0.15)),
     Maxv2 (abs (vec2 (q.x + 1.2, q.y - 5.1)) - vec2 (0.02, 0.15))));
  DMINQ (idTop);
  s = sign (q.z);
  q.z = abs (q.z);
  q -= vec3 (7.2 * vec2 (-1., tan (pi / (0.5 * nSec))), 2.1).xzy;
  d = PrCylDf (q.xzy, 0.07, 2.);
  q.z *= s;
  DMINQ (idPole);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 GrndNf (vec2 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return vec3 (normalize (vec3 (Fbm2 (p) - vec2 (Fbm2 (p + e.xy),
     Fbm2 (p + e.yx)), 4. * e.x)).xzy);
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * ltDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (vec3 (0.3, 0.4, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec4 ObjCol ()
{
  vec4 col4;
  vec3 c1, c2;
  float a;
  c1 = HsvToRgb (vec3 (gAng, 0.8, 1.));
  c2 = HsvToRgb (vec3 (fract (gAng + 0.5), 0.8, 1.));
  if (idObj == idPole || idObj == idBase || idObj == idCmid) a = atan (qHit.z, - qHit.x);
  if (idObj == idBase) col4 = vec4 (0.4, 0.2, 0., 0.) * (1. - 0.3 * step (5., length (qHit.xz)) * 
     (1. - smoothstep (0., 0.1, sin (128. * a) + 0.5)));
  else if (idObj == idTop) col4 = mix (vec4 (1., 0., 0., 0.1), vec4 (1., 1., 0., 0.1),
     smoothstep (0., 0.02, fract (1.1 * qHit.z + 0.5) - 0.5));
  else if (idObj == idFlr) col4 = (abs (qHit.x + 5.) < 2.45) ? vec4 (0.7, 0.4, 0.3, 0.1) *
     (0.8 + 0.2 * smoothstep (0., 0.03, fract (2. * qHit.x) - 0.1)) * (1. -
     0.4 * Fbm2 (64. * qHit.xz)) : vec4 (0.2, 0.7, 0.2, -1.);
  else if (idObj == idSpk) col4 = vec4 (0.2, 0.3, 0.2, 0.1);
  else if (idObj == idSup) col4 = vec4 (c1, -1.);
  else if (idObj == idTube) col4 = vec4 (0.8, 0.8, 0.85, 0.2) * (0.6 +
     0.4 * smoothstep (0., 0.02, fract (16. * qHit.y + 0.5) - 0.5));
  else if (idObj == idSeat) col4 = mix (vec4 (c1, 0.1), vec4 (c2, 0.1),
     step (0.5, fract (8. * qHit.z)));
  else if (idObj == idGrip) col4 = vec4 (c1, 0.1);
  else if (idObj == idCmid) col4 = vec4 (0.5, 0.5, 1., 0.1) *
     (0.7 + 0.3 * smoothstep (0., 0.1, cos (0.5 * nSec * a) + 0.5));
  else if (idObj == idPole) col4 = mix (vec4 (1., 0., 0., -1.), vec4 (1., 1., 1., 0.2),
     smoothstep (0., 0.03, fract (8. * qHit.y + a / (2. * pi)) - 0.1));
  else if (idObj == idRail) col4 = vec4 (0.8, 0.9, 0.9, 0.2);
  else if (idObj == idLitS) col4 = vec4 (((qHit.z < 0.) ? c1 : c2), -1.);
  else if (idObj == idLit) col4 = vec4 (1., 1., 0.7, -1.);
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 qHitG, col, vn;
  float dstObjG, dstObj;
  nSec = 36.;
  aRot = -0.05 * pi * tCur;
  dstObjG = GObjRay (ro, rd);
  qHitG = qHit;
  dstObj = ObjRay (ro, rd);
  if (min (dstObj, dstObjG) < dstFar) {
    if (dstObj < dstObjG) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
    } else {
      ro += dstObjG * rd;
      ro.xz = Rot2D (ro.xz, aRot);
      qHit = qHitG;
      vn = GObjNf (ro);
      vn.xz = Rot2D (vn.xz, - aRot);
    }
    col4 = ObjCol ();
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.1 * max (- dot (vn, ltDir), 0.) +
       0.8 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    else col = col4.rgb * (0.6 - 0.4 * dot (rd, vn));
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
    vn = GrndNf (ro.xz);
    col = (0.4 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
       0.8 * smoothstep (0.3, 0.7, Fbm2 (8. * ro.xz)))) *
       (0.2 + 0.8 * max (dot (vn, ltDir), 0.));
    col *= 0.7 + 0.3 * smoothstep (0., 0.5, length (ro.xz) - 9.3);
    col = mix (col, vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.));
  } else {
    col = SkyBgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.06 * pi;
  if (mPtr.z > 0.) {
    az -= 1.5 * pi * mPtr.x;
    el -= 0.5 * pi * mPtr.y;
  }
  el = clamp (el, -0.3 * pi, -0.02 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0.4, -30.);
  zmFac = 4.3 / (1. - 0.8 * abs (mod (az + pi, 2. * pi) - pi) / pi);
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  uv.x *= canvas.y / canvas.x;
  col = mix (vec3 (0.85), col, sqrt (sqrt (max (0., 0.95 - length (pow (abs (uv), vec2 (12.)))))));
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

vec2 rotate(vec2 c, float a) {
  return vec2(c.x*cos(a)-c.y*sin(a),c.x*sin(a)+c.y*cos(a));
}
void mainImage( out vec4 color, in vec2 c ) {
    c-=iResolution.xy/2.0;
    if(max(iResolution.x,iResolution.y)<1000.0)c*=2.0;
    float r=sqrt(c.x*c.x+c.y*c.y)/256.0;
    c.x=mod(c.x,256.0)-128.0;
    c.y=mod(c.y,256.0)-128.0;
    c=rotate(c,-iTime/2.0-abs(c.x/2.0)-abs(c.y/2.0));
    // c/=2.5; //try it
    color=vec4(int(abs(c.x)<60.0&&abs(c.y)<60.0));
    if(c.x<-12.0&&c.x>-36.0&&c.y>12.0)color=vec4(0.0);
    if(c.x<36.0&&c.x>12.0&&c.y<-12.0)color=vec4(0.0);
    if(c.y>12.0&&c.y<36.0&&c.x>12.0)color=vec4(0.0);
    if(c.y<-12.0&&c.y>-36.0&&c.x<-12.0)color=vec4(0.0);
    if(color.x*color.y*color.z>=1.0) {
        color=vec4(abs(sin(r-1.0)),abs(sin(r)),abs(sin(r+1.0)),0);
    }
}

#define PI 3.1415926538

// Size options
float spiralSize = 20.0; // Smaller is bigger; 0 would just be a rotating line
float spacing = 1.5; // Min. 1.0
float pulsationStrength = 0.5; // from 0.0 to 1.0
int armCount = 1; // 0 for rings; negative to flip the direction

// Speed options
float pulsespeed = 0.5; // 0.0 to disable pulsation
float rotationspeed = 2.0; // 0.0 to disable rotation; negative -> CW; positive -> CCW

// Color options
float gradient = 2.0; // 0.0 -> Fix to inner color
vec3 innerColor = vec3(1.0,1.0,1.0); // from 0.0 to 1.0 Red-Green-Blue
vec3 outerColor = vec3(0.0,0.0,0.25); // from 0.0 to 1.0 Red-Green-Blue

/*
 * Further info:
 * CW = Clockwise rotation
 * CCW = Counter clockwise rotation
 *
 * When using rings (0 arms) CW will move inwards while CCW moves outwards
 *
 * Please not that I did this as a learning project. It seemed both simple enough to do but also complex enough to learn 
 * writing shader effects.
 */


void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	vec2 pos = ( fragCoord.xy - iResolution.xy / 2.0 ) / max(iResolution.x, iResolution.y);

    float scaling = (sin((iTime * pulsespeed) + length(pos)) * pulsationStrength + 1.0) * spiralSize;
	float rotation = atan(pos.x, pos.y) * float(armCount) * 0.5 / PI * spacing + (iTime * rotationspeed);
	float intensity = max(0.0, 1.0 - mod(rotation - length(pos) * scaling , spacing));
    intensity = min(intensity, (1.0 - intensity) * intensity * 0.04 * iResolution.y); //Thanks to Shane for this anti-aliasing hack

	vec3 col = mix(innerColor,outerColor, length(pos) * gradient);
	fragColor = vec4( col * intensity, 1.0 );
}

