//----------------------------------------------------------------------------------------
//  3 out, 1 in...
vec3 hash31(float p)
{
    //from David Hoskin's "Hash without sine"
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);




    vec3 col;  
    float t1 = 4.5/2.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    //vec3 random1 = hash31(floor((iTime)/10.0+uv.x))*10.*.0;
    //vec3 random2 = hash31(1.+floor((iTime)/10.0+uv.x));
    float t2 = floor((iTime)/10.0+uv.x);
    vec3 random1 = (hash31(1.+t2)-vec3(.5))/4.;
    vec3 random2 = (hash31(1.+t2)-vec3(.5))/4.;
    vec3 random3 = (hash31(2.+t2)-vec3(.5))/4.;
    vec3 random4 = (hash31(3.+t2)-vec3(.5))/4.;

    float offset = .5;
    float scale2 = 1.5;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        //float scale1 = 1.0;
        for(int i=0;i<3;i++)
        {

            uv = triangle_wave(uv.yx-offset,scale)+triangle_wave(uv,scale);

            for(int k = 0; k < 3; k++){
                uv /= scale2;
                uv = triangle_wave((uv-random2[k])/(random4[k]+1.),scale+random3[k]).yx;

                scale /= 1.-(scale2+uv.x-uv.y)*col.x/5.;
                scale2 += (col.x);

            }


            col[c] = abs((uv.x)-(uv.y))/2.;

        }
	}

    fragColor = vec4((col*4.),1.0);

}

vec2 fluid(vec2 uv) {
    for (float i = 1.0; i <= 20.; i++) {
        uv.x -= iTime / 11. + sin(iTime + uv.y * -i) / i * 0.8 - sin(3.2);
        uv.y -= iTime / 22. + uv.x * i * 0.5 / i * 0.9;
    }
    return uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fluid(fragCoord / iResolution.xy * 6.5);

    float r = abs(sin(uv.y / 1.1));
    float g = abs(sin(uv.x / 2.2));
    float b = abs(sin(uv.x / 3.3));
    fragColor = vec4(r, g, b, 1.0);
}

