int qr(const int x,const int y){
    int r = 1;
    if (checkin(x,y, QR_LEN,QR_LEN)){
        for (int i = 0 ; i < QR_LEN ; i++) if (y == i) r = modi( QR[i] / pow2(x) + 1, 2 );
            return 1-r;
    }
}

float circleDist(vec2 p, float radius)
{
	return length(p) - radius;
}



float fillMask(float dist)
{
	return clamp(-dist, 0.0, 1.0);
}


vec4 drawLight(vec2 p, vec2 pos, vec4 color, float dist, float range, float radius)
{
	// distance to light
	float ld = length(p - pos);

	// out of range
	if (ld > range) return vec4(0.0);

	// shadow and falloff
	float shad = 1.;
	float fall = (range - ld)/range;
	fall *= fall;
	float source = fillMask(circleDist(p - pos, radius));
	return (shad * fall + source) * color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p2 = fragCoord.xy + vec2(0.5);
	vec2 c = iResolution.xy / 2.0;

	//float dist = sceneSmooth(p, 5.0);
	float dist = 1.;

	vec2 light1Pos = iMouse.xy;
	vec4 light1Col = vec4(0.75, 1.0, 0.5, 1.0);

	vec2 light2Pos = vec2(iResolution.x * (sin(iTime + 3.1415) + 1.2) / 2.4,iResolution.y * (sin(iTime + 3.1415) + 1.2) / 2.4);
	vec4 light2Col = vec4(1.0, 0.75, 0.5, 1.0);

	vec2 light3Pos = vec2(iResolution.x * (sin(iTime) + 1.2) / 2.4, 340.0);
	vec4 light3Col = vec4(1., 0.75, 1.0, 1.0);

	vec4 light4Col = vec4(0.5, 0.75, 1.0, 1.0);

	vec4 col = vec4(0.5, 0.5, 0.5, 1.0);
	col *= clamp(min(mod(p2.y, 10.0), mod(p2.x, 10.0)), 0.9, 1.0);

	col += drawLight(p2, light1Pos, light1Col, dist, 150.0, 6.0);
	col += drawLight(p2, light2Pos, light2Col, dist, 200.0, 8.0);
	col += drawLight(p2, light3Pos, light3Col, dist, 300.0, 12.0);
	qrLoad();
    ivec2 ir  = ivec2(iResolution.xy); 
    ivec2 ip  = ivec2(fragCoord.xy);
    ivec2 it  = ip*31/ivec2(ir.y) ;
    int width  = 31 * ir.x / ir.y;
    int movex = (width -31)/2;
    it.x -= movex;
    float qrf = float(1-qr(it.x-1, it.y-1));
    float t = iTime;
    float t1 = clamp((t-0.)*.2,0.,1.);
    vec2 ar   = vec2( iResolution.x/iResolution.y,1.);
    vec2 uv   = fragCoord/iResolution.xy;
    vec2 p1   = uv*ar;
    col*= (1.-qrf);
    col.xyz *=  1.7*smoothstep(0.,.8, t1*2.-length(p1-.5));
	fragColor = clamp(col, 0.0, 1.0);
}

float cylinder(vec3 position, float radius, float halfHeight, float cornerRadius) {
   vec2 d = vec2(length(position.xz), abs(position.y)) - vec2(radius, halfHeight) + cornerRadius; 
   return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0) - cornerRadius;
}

mat3 lookAtMatrix(vec3 from, vec3 to) {
    vec3 forward = normalize(to - from);
    vec3 right = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, forward);
    return mat3(right, up, forward);
}

mat3 rotationMatrixAxisAngle(vec3 axis, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    vec3 column1 = axis * axis.x * (1.0 - c) + vec3(c, axis.z * s, -axis.y * s);
    vec3 column2 = axis * axis.y * (1.0 - c) + vec3(-axis.z * s, c, axis.x * s);
    vec3 column3 = axis * axis.z * (1.0 - c) + vec3(axis.y * s, -axis.x * s, c);
    return mat3(column1, column2, column3);
}

float sdf(in vec3 position) { 
    float radius = 0.4;
    float halfHeight = 0.2;
    float cornerRadius = 0.01 + ((cos(iTime * 0.5) + 1.0) * 0.5) * 0.19;
    float d = cylinder(position, radius, halfHeight, cornerRadius);
    return d;
}

vec3 normal(vec3 position) {
    float epsilon = 0.001;
    vec3 gradient = vec3(
        sdf(position + vec3(epsilon, 0, 0)) - sdf(position + vec3(-epsilon, 0, 0)),
        sdf(position + vec3(0, epsilon, 0)) - sdf(position + vec3(0, -epsilon, 0)),
        sdf(position + vec3(0, 0, epsilon)) - sdf(position + vec3(0, 0, -epsilon))
    );
    return normalize(gradient);
}

float raycast(vec3 rayOrigin, vec3 rayDirection) {
    int stepCount = 128;
    float maximumDistance = 5.0;
    float t = 0.0;
    for (int i = 0; i < stepCount; i++) {
        if (t > maximumDistance) {
            break;
        }
        vec3 currentPosition = rayOrigin + rayDirection * t;
        float d = sdf(currentPosition);
        if (d < 0.0001) {
            return t;
        }
        t += d;
    }
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	float angle = 0.0;
	vec3 rayOrigin = vec3(1.0 * cos(angle), 0.4, 1.0 * sin(angle));
    vec3 targetPosition = vec3(0.0);
    mat3 cameraTransform = lookAtMatrix(rayOrigin, targetPosition);
    vec3 result = vec3(0.0);
    ivec2 sampleCount = ivec2(3.0, 3.0);
    for (int y = 0; y < sampleCount.y; y++) {
        for (int x = 0; x < sampleCount.x; x++) {
            vec2 uv = fragCoord + (vec2(float(x), float(y)) / vec2(sampleCount) - 0.5);
            uv = uv / iResolution.xy;
            uv = (uv * 2.0) - 1.0;
            uv.x *= iResolution.x / iResolution.y;
            vec3 rayDirection = normalize(vec3(uv, 1.5));
            rayDirection = cameraTransform * rayDirection;
            float t = raycast(rayOrigin, rayDirection);
            vec3 color = vec3(0.0);
            if (t > 0.0) {
                // same style that Inigo Quilez uses in his shaders
                vec3 position = rayOrigin + rayDirection * t;
                vec3 lightDirection = vec3(0.57735);
                vec3 n = normal(position);
                float diffuseAngle = max(dot(n, lightDirection), 0.0);
                // diffuse
                color = vec3(0.8, 0.7, 0.5) * diffuseAngle;
                // ambient
                color += vec3(0.2, 0.3, 0.4) * ((n.y + 1.0) * 0.5);
            }
            // gamma        
            color = sqrt(color);
            result += color;
        }
    }
    result /= float(sampleCount.x * sampleCount.y);
	fragColor = vec4(result, 1.0);
}

/**
 * @author singintime
 *
 * Draw an ellipse given its bounding box (top left corner, width, and height),
 stroke width, stroke color, and fill color.
 */
vec4 ellipse(vec2 uv, vec2 pos, float w, float h, float strokeWidth, vec4 strokeColor, vec4 fillColor) {
    vec2 p = uv - pos;
    vec2 pp = p * p;
    vec2 d = vec2(w, h) / 2.0;
    float s = strokeWidth / 2.0;
    vec2 dFillIn = d - s - 0.5;
    vec2 dFillOut = d - s + 0.5;
    float magFillIn = dot(pp, 1.0 / dFillIn / dFillIn);
    float magFillOut = dot(pp, 1.0 / dFillOut / dFillOut);
    float blend;

	if (magFillIn < 1.0) {
		return fillColor;
	}

    if (magFillOut < 1.0) {
        blend = (1.0 - magFillOut) / (magFillIn - magFillOut);
        return strokeWidth <= 0.0
            ? vec4(fillColor.rgb, blend)
            : fillColor * blend + strokeColor * (1.0 - blend);
    }

    vec2 dStrokeIn = d + s - 0.5;
    vec2 dStrokeOut = d + s + 0.5;
    float magStrokeIn = dot(pp, 1.0 / dStrokeIn / dStrokeIn);
    float magStrokeOut = dot(pp, 1.0 / dStrokeOut / dStrokeOut);

    if (magStrokeIn < 1.0) {
        return strokeColor;
    }

    if (magStrokeOut < 1.0) {
        blend = (1.0 - magStrokeOut) / (magStrokeIn - magStrokeOut);
        return vec4(strokeColor.rgb, blend);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float width = (0.3 + 0.2 * cos(iTime)) * iResolution.y;
	float height = (0.3 + 0.2 * sin(iTime)) * iResolution.y;
    float strokeWidth = 10.0 + 10.0 * cos(iTime / 5.0);

    // Background
	vec4 layer1 = vec4(0.65, 0.65, 0.65, 1.0);

	// Ellipse
	vec4 black = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
	vec4 layer2 = ellipse(uv, center, width, height, strokeWidth, black, white);

	// Blend the two
	fragColor = mix(layer1, layer2, layer2.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5f;

    //grt time(much easier than in vs2019)
    float t=iTime;

    float d=length(uv);
    float an=d+sin(t+.5f);

    vec2 ret=fract(5.*an*uv);

    // Output to screen
    fragColor = vec4(ret,1.,1.0);
}

#define PI 3.141596

vec2 rot(vec2 p, float an){
	mat2x2 rota = mat2x2( cos(an), -sin(an), 
						  sin(an), cos(an)  );
	return rota*p;
}

// checks if pixel is inside tri with angles an and (distance of lines from origin)==a
bool tri(vec2 p, vec3 an, float a){
	bool tri = true;
	for(int i=0; i<3 && tri; i++){ 
		tri = p.y > -a; // loop break = pixel.y not less then a from origin
		p = rot(p, radians(an[i])); // rotation for next side
	}
	return tri;
}

// absolute sort of triangle wave as time
float time(){
	return abs(0.3*tan(sin((iTime-1.0)/3.))+0.5);
}

// coordinate weirdness
vec2 weird(vec2 uv){
	uv *= 1.0+length(uv);
	return mix(vec2(uv.x, uv.y*sin(10.*uv.y)), vec2(uv.x*cos(10.*uv.x), uv.y), time()); // idk it's fun
}

// checkerboard pattern
float pattern(vec2 p, float a){
	return abs(mod(floor(a*p.x), 2.0)-mod(floor(a*p.y), 2.0));
}

// iq's exponential impulse
float impulse(float x, float k){
	x += 1.0/k;
	float h = k*x;
	return h*exp(1.0-h);
}	

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec3 col = vec3(0);
	float t;
	for(int i=0; i<3; i++){
		vec2 uv = (gl_FragCoord.xy-0.5*iResolution.xy)/iResolution.y;

		float d = distance(time(), 0.5); // at time()==0.5, the animation tips
		float boing = max(0.5*impulse((time()-0.5)*20., 4.0+float(i)), -1.0); // aberation impulse param
		uv *= sqrt(d*0.3*float(i)+1.0) - boing + 0.01*boing*boing*(3.0-2.0*boing); // aberation & scale animation

		vec2 orig = uv;
		uv *= 2.;
		uv += (mod(uv, 0.06)-0.06); // tiling, broken glass sorta effect
		uv = weird(uv);

		bool tri = tri(rot(uv, 1.1*iTime), vec3(120), 0.5); // rotating triangle
		col[i] = tri ? pattern(uv, 1.0) : 0.0; // tweak second pattern argument for different effects
		col[i] = mix(col[i], col[i] * clamp(pattern(gl_FragCoord.xy, 0.333)*abs(1.0/length(uv)), 0.0, 1.0), 1.0/length(orig)); // mini pattern
	}
	fragColor = vec4(col, 1.);
}

