float map(float x, float inMin, float inMax, float outMin, float outMax) {
    return (x - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
}

vec3 hs(vec3 c, float s){
    vec3 m=vec3(cos(s),s=sin(s)*.5774,-s);
    return c*mat3(m+=(1.-m.x)/3.,m.zxy,m.yzx);
}

mat2 rotate(float a) {

	float c = cos(a),

		s = sin(a);

	return mat2(c, -s, s, c);

}

float opSmoothUnion( float d1, float d2, float k )  //smooth merges two objects

{

float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );

return mix( d2, d1, h ) - k*h*(1.0-h);

}



//_+_+_+_+_+_+_SHAPING FUNCTIONS_+_+_+_+_+_+_+_
float GetDist(vec3 p) {



	float d = 1.; //Just to have the d var declaired

//	float perStep = 1/FRACT_STEPS;

	for (int i = 0; i < FRACT_STEPS ; i++){

if(InfPre == 1) p = mod(p+2.5, 5.)-2.5;   //Createts infinity

		if(MirrorX == 1) p.x = abs(p.x); //Mirror X
		if(MirrorY == 1) p.y = abs(p.y); //Mirror Y
		if(MirrorZ == 1) p.z = abs(p.z); //Mirror Z

		p.x -= TransX
        +iMouse.x*Range
        +TimeTransX*sin(iTime*0.2);
		p.y -= TransY+iMouse.y*Range
        +iMouse.y*Range
        +TimeTransY*cos(iTime*0.1);;
		p.z -= TransZ;


		p.xy *= rotate(RotX+sin(iTime*0.2)*TimeRotX); //Audio rct
		p.xz *= rotate(RotY+cos(iTime*0.1)*TimeRotY);
		p.yz *= rotate(RotZ);

    p.xz *= rotate(p.y*TwistY); //Twist

if(InfPost == 1)		p = mod(p+2.5, 5.)-2.5;   //Createts infinity

}

if (Sphere == 1){
		vec4 sphere = vec4(SphereTX, SphereTY, SphereTZ, SphereScale);
	  float sphereDist =  length(p-sphere.xyz)-sphere.w;
    if(Merge == 1) d = opSmoothUnion(sphereDist, d, ShapeMix);
    else d = min(sphereDist,d);
  }


		 if (Rectangle == 1){//Adds in the Rectangle
			 vec4 rec = vec4(0.0, 0.0, 0.0, RecScale);
			 float recDist = length(max(abs(p) - rec.w, 0.));
			 if(Merge == 1) d = opSmoothUnion(recDist, d, ShapeMix);
       else d = min(recDist,d);
		 }


		// d = min(sphereDist,d);


	//}
    return d;
}

vec3 RayMarch(vec3 ro, vec3 rd) {
	vec3 dO = vec3(NearClip,0.,10.);  //  x = distance ray has traveled, y = Iterations to hit, z = Closest distance to surface

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO.x;
        float dS = GetDist(p);
        dO.x += dS;
				dO.z = min(dS, dO.z);
				dO.y = float(i);
        if(dO.x>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 1, -6);
    //lightPos.xz += vec2(sin(TIME), cos(TIME))*2.; //light movement
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).x;
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord, in vec3 _ro, in vec3 _rd)

{

    vec2 uv = (fragCoord+fragCoord - (iResolution.xy) ) / iResolution.y ;


    vec3 color = vec3(0);

		float prox = 0.0;

    vec3 ro = vec3(0, 0, -6+
      (InfPre+InfPost));  //Audio rct
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    ro = _ro;
    rd = _rd;


    vec3 d = RayMarch(ro, rd);

    vec3 p = ro + rd * d.x;

    float dif = GetLight(p);  //lighting
    //color = vec3(dif);				//lighting

		vec3 n = GetNormal(p);


    //+_+_+_+_+_+_+COLORING_+_+_+_+_+_+_

  //vec3 tex =  textureLod(syn_UserImage, _uv, 0.0).rgb;

		color.r = (d.y*0.05-(1.-step(d.x*0.04, 1.)))*float(Iterations);

    color = hs(color, IHue);

    color.g = (1.-(d.z+step(d.x*0.04, 1.)))*Proximity
    // *1.-tex.r
    ;						//G

    color = hs(color, PHue);

    color.b = (1.-d.x*0.04)*Distance;



    color = hs(color, DHue);

    color += dif*light;       //light
  //  color += hs(color, 5*dif*light);       //light hue rotate

		  color = hs(color, d.y*DepthHue); //iterations to hue rot

//		color -= dif*0.5;



    fragColor = vec4(color,1.0);
}

#define QAA	2                                     

const float	sideAngle       = radians(15.0);
const float	horizontalAngle = radians(14.0);
const float	cameraSize      = 4.5;
const float	aspectRatio     = 0.75;
const float quiltColumns	= 8.0;
const float quiltRows		= 6.0;
const float nearClip        = 8.0; 
void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2	coord = fragCoord * vec2(quiltColumns,quiltRows) / iResolution.xy;
	vec2	fract = fract(coord);
	vec2	floor = floor(coord);
	float	imageId = floor.x + floor.y * quiltColumns;
	float	valueId = imageId / (quiltColumns * quiltRows - 1.);
	float	dist = (cameraSize / 2.) / tan(horizontalAngle / 2.);
	float	minCam = -(cameraSize / 2.) - tan(sideAngle / 2.) * dist;

	vec3	camPos = mix(vec3(minCam,0,dist),vec3(-minCam,0,dist),valueId);
	vec3	screenPos = vec3((fract.x-0.5)*cameraSize,(fract.y-0.5)*cameraSize / aspectRatio,0);

	//vec4	color = vec4( vec3(valueId), 1 );

	vec4	color;
	vec3 	dirRay = normalize(screenPos-camPos);
    dirRay.z = -dirRay.z;
	mainImage( color, fract * iResolution.xy, screenPos - nearClip * dirRay, dirRay );

    fragColor = color;

}

/*
Filtering the input of a shader with a simple distance function.
*/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  // Normalized pixel coordinates -0.5..0.5
  vec2 uvCentered = fragCoord.xy - (iResolution.xy * vec2(0.5));
  //The distance from the center of the screen, calculated for each pixel.
  float uvDistance = sqrt(uvCentered.x * uvCentered.x + uvCentered.y * uvCentered.y);

  // Normalized mouse coordinates -0.5..0.5
  vec2 mouseCentered = (iMouse.xy - (vec2(0.5) * iResolution.xy)) ;
  //convert the coordinates of the mouse to a simple distance from the center of the shader area.
  float mouseDistance = sqrt(mouseCentered.x * mouseCentered.x + mouseCentered.y * mouseCentered.y);


  float radius = iMouse.y / 2.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+(fragCoord.xy / iResolution.xy).xyx+vec3(0,2,4));
    // Output to screen, using a step function to filter based on distance from mouse click.
    fragColor = vec4(step(uvDistance, mouseDistance) * col.xyz,1.0);
}

