vec3 lattice (float t ) {



  float m = t;
  float mx = floor((m-2.)/3.);
  float my = floor((m-1.)/3.);
  float mz = floor((m-0.)/3.);

  float n= m+1.;
  float nx = floor((n-2.)/3.);
  float ny = floor((n-1.)/3.);
  float nz = floor((n-0.)/3.);





  vec3 a =  
  mix(
      vec3(
          rand(mx)-.5,
          rand(my)-.5,
          rand(mz)-.5
      ),
      vec3(
          rand(nx)-.5,
          rand(ny)-.5,
          rand(nz)-.5
      ) , 
      fract(t)
  );
  return a;
}
