float building(vec3 p) {

  p.xz = abs(p.xz)- 100.;

  p = rep(p, vec3(0.,50.,0.));

  float floors = box(p, vec3(110,11.1,110));
  float collumns = box(p, vec3(10,40,10));

  float all = min(floors,collumns);

  if ( floors == all) {
    matter = 1;
  }
  if ( collumns == all) {
    matter = 2;
  }
  return all;


}



