vec3 norm(vec3 p) {
  vec2 off=vec2(0.01,0.0);
  return normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx))+0.000001);
}

float getao(vec3 p, vec3 n, float dist) {
  return clamp(map(p+n*dist)/dist,0.0,1.0);
}

float sss(vec3 p, vec3 l, float d) {
  return smoothstep(0.0,1.0,map(p+l*d)/d);
}

float rnd(float t) {
  return fract(sin(t*789.451)*7541.223);
}

float rnd(vec2 t) {
  return fract(dot(sin(t*vec2(789.451)+t.yx*vec2(842.544)),vec2(7541.223)));
}

float tick (float t) {
  float i = floor(t);
  float r = fract(t);
  for (int z = 0; z < 2; z++) {
    r = smoothstep(0.,1.,r);
  }

  return i + r;
}

vec3 flit(float t) {

  vec3 x = normalize(vec3(1));
  float t1 = tick(t);
  float t2 = tick(t * .71);
  float t3 = tick(t * .55);
  x.yz *= rot(t1);
  x.xz *= rot(t2);
  x.yx *= rot(t3);

  return x;
}

