// this create a rectangle, from uv and size
float rectangle (vec2 uv, float size) {
	vec2 s = abs(uv) - vec2(size);
    float Patrat = length(max(s, .0));
    Patrat = smoothstep(.001, .0, Patrat);
    return Patrat;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // all colors
    vec3 col = vec3(0.0);    // background black 
    vec3 blue = vec3(0.0, 0.0, 1.0); // blue flag
    vec3 yellow = vec3(1.0, 1.0, 0.0); // yellow flag
    vec3 red = vec3(1.0, 0.0, 0.0); // red flag

    // get uv area 
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    // set a new area for draw 
    vec2 new_uv = uv;


    // create a flagpole 
    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    new_uv = uv;
    new_uv.x += .335;
    float flagpole = smoothstep(.0176, .0155, length(new_uv.x));
    col += flagpole;

    // translation of uv to the left area of flagpole
    uv.y -= .0;
    uv.x -= -.321;

    //create animation with color 
    float windColor= .1;

    // wind effect
    new_uv.x = uv.x;
    new_uv.x += -1.0-sin(-iTime);

    float mod = smoothstep(0.9, 0.0, length(abs(new_uv.x+(new_uv.y*-.5))));

    // shadow wind
    uv.y += mod * 0.1;
    col -= mod * windColor;

    // up and down flag 
    uv.y +=0.18 * sin(-0.11 * (iTime))/mod*mod;
    col -= mod*windColor;

    // flag created with the new UV and Patrat 
    new_uv = uv;
    new_uv.x *= .55;
    float base = rectangle(new_uv, .3);

    float flag_X = new_uv.x;
    float flag_Y = new_uv.y* base;


    if (flag_X > 0.0 && flag_X < 0.6  && flag_Y > 0.0) 
    { 		       
		float size = 3.0;
		if (flag_X < 0.33 / size) 
		{
			// Output to screen fill the screen with blue
			//fragColor = vec4(0,0,1,0);
            col = blue;

		} else if (flag_X < 0.6 / size) {     
			// fill the 2/3 of size with yellow color
			col = yellow;

		} else if (flag_X < 0.9 / size) {

			// fill the last 1/3 with the red color 
			col=red;
		}
    }

    fragColor = vec4(col,1.0);
}

