    dp = abs(dot(normalize(ba),vec2(0,1)));
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return lpnorm( pa - ba*h , 2.)+.25*sabs(p.x, .5)+.35*sabs(p.y, .5);
}


