        case 2:
            return sdNum2(p);
        case 3:
            return sdNum3(p);
        case 4:
            return sdNum4(p);
        case 5:
            return sdNum5(p);
        case 6:
            return sdNum6(p);
        case 7:
            return sdNum7(p);
        case 8:
            return sdNum8(p);
        case 9:
            return sdNum9(p);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord/iResolution.xy;
    vec2 grid = vec2(.2,.5);
    vec2 ip = floor(p/grid);
    p = mod(p, grid)/grid*2.-1.;
    p.y*=iResolution.x/iResolution.y;
    p.y*=.8; // ???

	fontWeight = .4+(sin(iTime*2.))*.015;//-clamp(sin(iTime*1.5)*2.,0.,1.)*.19;
    fontSize = .9;
    float num = ip.x+(1.-ip.y)*5.;// + floor(exp(clamp(sin(iTime*5.),0.,1.))*10.) + floor(sin(iTime*.5)*10.);
    float d = sdNumFont(p, fontSize, num);
    d = 1.-smoothstep(.0,.015,d);

    vec3 col = mix(vec3(0.102,0.102,0.102), vec3(.8, .77, .7), d);

    // Draw Skeleton
    col = max(col, vec3(.8, .77, .7)*.3*smoothstep(0.02, 0., abs(sdAllSeg(p/fontSize)+fontWeight)-.002));

	fragColor = vec4(col,1.0);
}

