#define PI 3.1415
#define ITERATIONS 25.

float ran21(vec2 uv) {
    return fract(cos(dot(cos(uv.x*uv.y)-32.2,tan(uv.x/uv.y)-23.5)*1322.24)*432122.62);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;
    vec2 uvb = uv;

    vec3 col = vec3(0);
    for(float i=1.;i<=ITERATIONS;i++) {
        float cur = i/ITERATIONS;
        uv = uvb;
        uv *= (25.*ITERATIONS/(ITERATIONS+i))-(cur*1.)/(length(uvb/uv));
        uv.y += iTime*0.6;

        vec2 gv = fract(uv)-.5;
        vec2 id = floor(uv);

        gv.x *= (ran21(id)>.5) ? -1. : 1.;
        vec2 ruv = gv-sign(gv.x+gv.y+.001)*.5;
        float tile = smoothstep(.01,-.01,abs(length(ruv)-.5)-.1);
        float rot = atan(ruv.x,ruv.y)/PI;

        float mul = mod(id.x+id.y,2.)==1. ? -1. : ITERATIONS/i;
        float h = smoothstep((ITERATIONS/cur+.1)/gv.x,cur,sin(abs(rot*cur*mul+iTime)*PI)*.25+.75);

        col.b -= (h*tile*cur)/(h/i);
        col.b *= .85-((h/i)/(h*tile*cur));
        col.gb *= .95-((col.r + col.g )/(h*i));

        col = max(col,h*tile*cur);
    }

    fragColor = vec4(col,1.0);
}

vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

vec2 boxIntersection( in vec3 ro, in vec3 rd, vec3 boxSize, out vec3 outNormal ) 
{
    vec3 m = 1.0/rd; // can precompute if traversing a set of aligned boxes
    vec3 n = m*ro;   // can precompute if traversing a set of aligned boxes
    vec3 k = abs(m)*boxSize;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return vec2(-1.0); // no intersection
    outNormal = -sign(rd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
    return vec2( tN, tF );
}

float plaIntersect( in vec3 ro, in vec3 rd, in vec4 p )
{
    return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

float MAX_DISTANCE = 200.0;
float gamma = 2.2; 


vec3 rotateX(vec3 a, float angle)
{
    vec3 b = a;
    b.z = a.z * cos(angle) - a.y * sin(angle);
    b.y = a.z * sin(angle) + a.y * cos(angle);
    return b;
}

vec3 rotateY(vec3 a, float angle)
{
    vec3 b = a;
    b.x = a.x * cos(angle) - a.z * sin(angle);
    b.z = a.x * sin(angle) + a.z * cos(angle);
    return b;
}

vec3 rotateZ(vec3 a, float angle)
{
    vec3 b = a;
    b.x = a.x * cos(angle) - a.y * sin(angle);
    b.y = a.x * sin(angle) + a.y * cos(angle);
    return b;
}


vec3 castRay(inout vec3 ro, inout vec3 rd) {

    vec2 minIt = vec2(MAX_DISTANCE);
    vec3 n = vec3(0);
    vec3 col;

    vec3 spherePos = vec3(-3.0, 0, -1.0);
    vec2 it = sphIntersect(ro, rd, spherePos, 1.0);
    if (it.x > 0.0 && it.x < minIt.x) {
        col = vec3(1.0, 0, 0);
        n = normalize(ro + rd * it.x - spherePos);
        minIt = it;
    }

    spherePos = vec3(4.0, 3.0, 3.0);
    it = sphIntersect(ro, rd, spherePos, 3.0);
    if (it.x > 0.0 && it.x < minIt.x) {
        col = vec3(1.0, 0, 1.0);
        n = normalize(ro + rd * it.x - spherePos);
        minIt = it;
    }

    vec3 boxSize = vec3(1);
    vec3 boxN;
    it = boxIntersection(ro, rd, boxSize, boxN);
    if (it.x > 0.0 && it.x < minIt.x) {
        col = vec3(0.1, 0.8, 0.2);
        n = boxN;
        minIt = it;
    }

    vec4 planeN = vec4(0, -1, 0, -1);
    it = vec2(plaIntersect(ro, rd, planeN));
    if (it.x > 0.0 && it.x < minIt.x) {
        col = vec3(1.0, 1.0, 1.0);
        n = planeN.xyz;
        minIt = it;
    }

    if (minIt.x == MAX_DISTANCE) {
        return vec3(-1.0);
    }

    vec3 light = normalize(vec3(sin(iTime), 1, cos(iTime)/5.0));
    // vec light = normalize(vec3(1, 1, 0));

    float ambient = 0.1;
    float diffuse = max(0.0, dot(n, light)) * 0.4 + 0.4;
    float specular = pow(max(0.0, dot(reflect(rd, n), light)), 64.0);

    ro += rd * (minIt.x - 0.001);
    rd = n;

    return (ambient + diffuse) * col + specular;
}

vec3 traceRay(vec3 ro, vec3 rd) {
    vec3 col = castRay(ro, rd);

    if (col.x == -1.0) {
        return vec3(0.1,0.5,0.8);
    }

    vec3 light = normalize(vec3(sin(iTime), 1, cos(iTime)/5.0));

    ro -= rd * 0.01;

    vec3 hitColor = castRay(ro, light);    
    if (hitColor.x != -1.0) {
        col *= 0.5;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/iResolution.y;

    vec2 uv = (fragCoord/iResolution.xy - 0.5) * iResolution.xy/iResolution.y;    

    vec3 ro = vec3(0, 2, -10);
