    ro = rotateZ(ro, 0.25);    
    ro = rotateY(ro, iTime * 0.4);

    rd = rotateX(rd, -0.15);
    rd = rotateY(rd, iTime * 0.4);

    vec3 col = traceRay(ro, rd);

    col = pow(col, vec3(1.0/gamma));

    fragColor = vec4(col, 1.0);    
}

