// Increase for better quality of light.
const int DUST = 128;

// The color of the rays of light. 
const vec3 LIGHT_COLOR = vec3(1.6, 1.6, 1.);

// ----------------------------- spheres -----------------------------

const vec3 BLACK = vec3(0.);
const vec3 WHITE = vec3(1.);

struct Sphere {
	float r;	
    vec3 color;
};

const int N = 3;

const Sphere spheres[N] = Sphere[N](
    Sphere(30.,    vec3(0.3, 0.0, 0.0)), 
    Sphere(20.,    vec3(0.0, 0.3, 0.1)) ,
    Sphere(10.,    vec3(0.05, 0.1, 0.25))
);

const float minValue = 0.5; // The lower this is, the fuller are the spheres (the components are larger).
const float details = 2.5;  // The higher this is, the more components each sphere will have. 

// Just a very simple noise function, used to create the components on each sphere.
float snoise3d(vec3 v) {
    float t = (v.x+2.)*v.y+v.z*v.z*1.4+((cos(v.x*0.42-v.z*1.3)+sin(v.y*0.5-5.*v.x))*cos(v.z+v.y*2.));
    return t - floor(t);
}




// ----------------------------- ray hit -----------------------------

const float MAX_DIST = 1000000000.;

struct HitInfo {
    vec3 hitPoint;
    vec3 normal;
    float dist;

    vec3 color;
};

const HitInfo NO_HIT = HitInfo(vec3(0.), vec3(0.), MAX_DIST, BLACK);


mat3 rotateZ(float angle){
    return mat3(
        cos(angle),  sin(angle), 0.,
        -sin(angle), cos(angle), 0.,
        0., 0., 1.);
}

/**
 * Given the origin and direction of the ray, check if it hits the given sphere.
 */
HitInfo sphereHit(Sphere sphere, float angle, vec3 origin, vec3 direction){
    float dist = MAX_DIST;
    // |origin + t*direction|^2 = r^2
    // |origin|^2 +2t<direction,origin> + t^2 |direction|^2 = r^2
    vec3 v = origin;                     
    float a = dot(direction, direction);         
    float b = 2.*dot(direction,v);               
    float c = dot(v,v)-sphere.r*sphere.r;                      
    // solve a*t^2 + b*t + c = 0
    float disc = b*b-4.*a*c;
    if (disc<0.)
        return NO_HIT; // no intersection with the sphere

    dist = (-b - sqrt(disc))/(2.*a);
    vec3 hitPoint = (origin + dist*direction);

    // Use the noise function to decide whether this part of the sphere exists.
    float x = snoise3d(details*rotateZ(angle)*hitPoint/100.);
    if (x>minValue)    
        return HitInfo(hitPoint, normalize(hitPoint), dist, sphere.color);

    // Didn't hit the "front" part of the sphere. Check the back part.
    dist = (-b + sqrt(disc))/(2.*a);
    hitPoint = (origin + dist*direction);
    x = snoise3d(details*rotateZ(angle)*hitPoint/100.);
    if (x>minValue)  
        // note that the back part should have more light that the front part.
        return HitInfo(hitPoint, normalize(hitPoint), dist, sphere.color*1.3); 

    return NO_HIT; // no intersection with the sphere       
}


/**
 * Given the origin and direction of the ray, check if it hits one of the spheres.
 */
HitInfo sceneHit(vec3 origin, vec3 direction){

    HitInfo info = NO_HIT;
    float dist = MAX_DIST;
    for (int i=0; i<N; i++){
        HitInfo sphereInfo = sphereHit(spheres[i], float(i+1)*iTime/3., origin, direction);
        if (sphereInfo.dist<dist){
            info = sphereInfo;
            dist = sphereInfo.dist;
        }
    }

    return info;
}



// ----------------------------- ray color -----------------------------

// Check if there is a line of sight from the given position to the origin.
bool lineSightToOrigin(vec3 position){
    HitInfo lightInfo = sceneHit(position, -normalize(position));
    float len = length(position);
    return (lightInfo.dist>len);
}

vec3 rayColor(vec3 origin, vec3 direction){

    // Check if the ray hits one of the spheres.
    HitInfo info = sceneHit(origin, direction);
    vec3 color = info.color;

    // Add the light as if it hits "dust" in the way.
    float dist = min(info.dist, 500.);
    for (int i=0; i<DUST; i++){
        vec3 location = origin+dist*(float(1+i)/float(1+DUST))*direction;
        if (lineSightToOrigin(location))
            color += LIGHT_COLOR/float(DUST);
    }
    return color;

}


