void updateCamera(out vec3 origin, out vec3 forward, out vec3 right, out vec3 up){
    //origin = 100. *vec3(cos(iTime),sin(iTime),0.);
    origin = 100. *vec3(1.,0.,0.);

    forward = -origin/100.;
    right = vec3(forward.y, -forward.x , 0.);
    up = vec3(0., 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 origin, forward, right, up;
    updateCamera(origin, forward, right, up);


    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;

