    // Output to screen
    fragColor = vec4(color ,1.0);

}

vec2 irwin_hall_noise_2D( uvec2 coord, uint seed, int N )
{
    uvec2 rand = uvec2( 131071u * coord.x + 31u * coord.y, 127u * coord.x + 8191u * coord.y );
    rand = rand * 524187u + seed * 7u;  
    rand = rand * 2147483647u + seed * 3u;                 
    vec2 result = vec2(0.);
    for( int i = 0; i < N; ++i )
        { rand = rand * rand | 1u; result.xy += vec2( rand ); }
    float n = float(N);
    float s = sqrt( 12. / n );
    return s * ( result * 2.32830644e-10 - n / 2. );
}

void mainImage( out vec4 fcolor, in vec2 fcoord )
{
    const int N = 4;
    const float buckets = 15.;
    const float scale = inversesqrt( float( 12 * N ) );
    vec2 noise = .5 + scale * irwin_hall_noise_2D( uvec2( fcoord ), uint( iFrame ), N );
    fcolor.xyz = step( +noise, +floor( fcoord / iResolution.xy * buckets + 1. ) / buckets ) *
                 step( -noise, -floor( fcoord / iResolution.xy * buckets + 0. ) / buckets ) * 
                 mat3x2( .9, .01, .1, .1, .01, .9 );
    fcolor.xyz = pow( fcolor.xyz + .002, vec3( .4545 ) );
}

// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define MAX_STEPS 32
#define MAX_DIST 6.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define MATERIAL_STEAL 0
#define MATERIAL_WALL 1
#define ZERO (min(iFrame,0))

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
    const vec3 s = vec3(27, 111, 57);
    vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

// thx:iq https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float sdSimpleCylinder(vec3 p, float r, float h){
    float d = length(p.xz)-r;
    d = max(abs(p.y)-h,d);
    return d;
}

// vertical version
float sdTorus( vec3 p, vec2 t )
{
    vec2 q = vec2(length(p.xy)-t.x,p.z);
    return length(q)-t.y;
}

// thx:iq https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdEquilateralTriangle( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

// thx:iq https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdUnevenCapsule( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));
    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
    return dot(p, vec2(a,b) ) - r1;
}

float dFakeSpring(vec3 p){
    for(int i = 0; i<4; i++){
        p.z = abs(p.z);
        p.z-=0.025;
    }

    float d = sdTorus(p,vec2(0.05,0.023));
    return d;
}

float steelMesh(vec3 p){
    float d = dFakeSpring(p);

    p.yz = abs(p.yz);
    p.z-=0.2;
    p.y-=0.32;
    p.y+=0.22;
    p.yz*=Rot(radians(-35.0));
    float d2 = sdSimpleCylinder(p,0.02,0.5);
    d = opSmoothUnion(d,d2,0.05);
    return d;
}

vec2 GetDist(vec3 p) {
    p.y+=iTime*0.1;
    p += noise3d(p*300.0)*0.002;   
    vec3 prevP = p;

    p.x = mod(p.x,1.2)-0.6;
    p.y = mod(p.y,1.0)-0.5;
    p.xz*=Rot(radians(90.0));

    float d = dFakeSpring(p);
    d = steelMesh(p);
    p = prevP;

    p.x+=0.6;
    p.y+= 0.5;
    p.x = mod(p.x,1.2)-0.6;
    p.y = mod(p.y,1.0)-0.5;

    p.xz*=Rot(radians(90.0));
    float d2 = steelMesh(p);
    d = min(d,d2);

    p = prevP;
    float wallD = p.z+0.01;
    return combine(vec2(d,MATERIAL_STEAL),vec2(wallD,MATERIAL_WALL));
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

vec3 iconBg(vec2 p, vec3 col){
    float d = sdEquilateralTriangle(p)-0.2;
    col = mix(col,vec3(1.0,1.0,0.0),S(d,0.0));
    d = abs(sdEquilateralTriangle(p)-0.2)-0.05;
    col = mix(col,vec3(0.0),S(d,0.0));
    return col;
}

vec3 hazrdIcon(vec2 p, vec3 col){
    p*=5.0;
    vec2 prevP = p;
    col = iconBg(p,col);

    p.y+=0.2;
    p.x = abs(p.x);
    p.x-=0.3;
    p*=Rot(radians(30.0));
    float d = length(p)-0.4;
    d = max(-(length(p-vec2(0.1,0.0))-0.31),d);
    col = mix(col,vec3(0.0),S(d,0.0));

    p = prevP;
    p.y-=0.4;
    d = length(p)-0.4;
    d = max(-(length(p-vec2(0.0,0.1))-0.31),d);
    col = mix(col,vec3(0.0),S(d,0.0));

    p = prevP;
    p.y-=0.05;
    d = abs(length(p)-0.35)-0.05;
    col = mix(col,vec3(0.0),S(d,0.0));

    return col;
}

vec3 warningIcon(vec2 p, vec3 col){
    p*=5.0;
    vec2 prevP = p;
    col = iconBg(p,col);

    float d = sdUnevenCapsule(p,0.1,0.2,0.7);
    d = min(length(p-vec2(0.0,-0.35))-0.15,d);
    col = mix(col,vec3(0.0),S(d,0.0));

    return col;
}

vec3 drawHazardIcons(vec2 p, vec3 col){
    vec2 prevP = p;
    p.y+=0.5;
    p.x = mod(p.x,1.2)-0.6;
    p.y = mod(p.y,1.0)-0.5;
    col = warningIcon(p,col);

    p = prevP;
    p.x+=0.6;
    p.x = mod(p.x,1.2)-0.6;
    p.y = mod(p.y,1.0)-0.5;
    col = hazrdIcon(p,col);

    return col;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    vec3 np = p;
    np.y+=iTime*0.1;
    float nn = noise3d(np*80.0)*0.12;
    if(mat == MATERIAL_STEAL){
        col = diffuseMaterial(n,rd,p,vec3(0.3)+nn+nn);
    }
    if(mat == MATERIAL_WALL){
        col = diffuseMaterial(n,rd,p,(vec3(0.3,0.2,0.1)+nn+nn)+drawHazardIcons(np.xy,col)*0.2);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float handShakeY = noise3d(vec3(iTime*1.0,iTime*1.1,iTime*1.2))*0.05;
    float handShakeX = noise3d(vec3(iTime*1.1,iTime*1.2,iTime*1.1))*0.2;

    vec3 ro = vec3(handShakeX, handShakeY, 2.0);
    ro.xz *= Rot(radians(sin(iTime*0.5)*5.0));

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
    } else {
        col = vec3(0.3);
    }

    // gamma correction
    col = pow( col, vec3(0.8545) );    

    fragColor = vec4(col,1.0);
}

const float pi = 3.1415926535897932;

float atanM(float y, float x){
    if(x >= 0.){
        return atan(y/x)/pi/2.+.75;
    }
    return (pi+2.*atan(y/x))/4./pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int amount = 2;
    float width = 2.5;
    float speed = 0.05;
    float speed2 = -0.075;
    float speed3 = 0.10;
    vec3 color1 = vec3(1.0, 1.5, 2.0);
    vec3 color2 = vec3(-0.1, 0.0, 0.1);
    vec3 color12 = vec3(1.0, 2.5, 2.0);
    vec3 color22 = vec3(-0.11, 0.11, 0.11);
    vec3 color13 = vec3(2.0, 2.5, 2.0);
    vec3 color23 = vec3(-0.01, 0.01, 0.01);


    vec2 R = iResolution.xy;
    vec2 uv = fragCoord-R/2.;
    vec3 col;
    vec3 col2;
    vec3 col3;
    float d = length(uv);
    float d2 = length(uv)/50.;
    float d3 = length(uv)/75.0;
    d -= iTime*speed;
    d2 -= iTime*speed2;
    d3 -= iTime*speed3;

    float mascara = smoothstep(R.y/2.+width*4., R.y/2.-width*4., tan(length(uv.x*30.) / length(uv.y/-30.)));
    float mascara2 = smoothstep(R.y/2.+width*4., R.y/2.-width*4., (length(uv.x/8.) * length(uv.y/-8.)));
    float mascara3 = smoothstep(R.y/2.+width*4., R.y/2.-width*4., (length(uv.x) + length(uv.y)));

    col = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d*width)*float(amount))+1.)/2.;
    col2 = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d2*width)*float(amount))+1.)/2.;
    col3 = vec3(sin((2.*pi*atanM(uv.y, uv.x)+d3*width)*float(amount))+1.)/2.;
    col = color1+col*(color2-color1);
    col *= mascara2;
    col2 = color23+col3*(color13-color23);
    col2 *= mascara;
    col3 = color22+col3*(color12-color22);
    col3 *= mascara3;
    fragColor = vec4(col-col2-col3,1.0);
}

// lpnorm by gaz
// https://www.shadertoy.com/view/slG3Rz
float lpnorm(vec2 p, float n){vec2 t=pow(abs(p),vec2(n));return pow(t.x+t.y,1./n);}

float sabs(float x,float k) {
	float a = (.5/k)*x*x+k*.5;
	float b = abs(x);
	return b<k ? a : b;
}

// iq's segment function
float sdSegment( in vec2 p, in vec2 a, in vec2 b ){
    //p.x += sin(p.y*50.)*.01;
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return lpnorm( pa - ba*h , (1.-abs(sin(iTime*.5)))*50.+2.)-.1*sabs(p.x, .5)-.1*sabs(p.y, .5);
}


