        case 6:
            return sdNum6(p);
        case 7:
            return sdNum7(p);
        case 8:
            return sdNum8(p);
        case 9:
            return sdNum9(p);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord/iResolution.xy;
    vec2 grid = vec2(.2,.5);
    vec2 ip = floor(p/grid);
    p = mod(p, grid)/grid*2.-1.;
    p.y*=iResolution.x/iResolution.y;
    p.y*=.8; // ???

	fontWeight = .17;//-clamp(sin(iTime*1.5)*2.,0.,1.)*.19;
    fontSize = .7;
    float num = ip.x+(1.-ip.y)*5.;// + floor(exp(clamp(sin(iTime*5.),0.,1.))*10.) + floor(sin(iTime*.5)*10.);
    float d = sdNumFont(p, fontSize, num);
    d = 1.-smoothstep(.0, 1./iResolution.x*19.,d);

    vec3 col = mix(vec3(0.102,0.102,0.102), vec3(.8, .77, .7), d);

    // Draw Skeleton
    col = max(col, vec3(.8, .77, .7)*.3*smoothstep(1./iResolution.x*19., 0., abs(sdAllSeg(p/fontSize)+fontWeight)-.002));

	fragColor = vec4(col,1.0);
}

