vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val);
vec2 Hashv2v2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

mat3 flMat;
vec3 qHit, sunDir, flDisp;
vec2 gId;
float tCur, dstFar, hgSize, grLim, hVel, heliSz;
int idObj;
const int idHeli = 1, idRotorM = 2, idRotorT = 3, idSkd = 4, idGun = 5;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{  // (from "Piz Gloria With Helicopter")
  vec3 q;
  vec2 csRotor;
  float dMin, d, r, s;
  csRotor = sin (10.3 * pi * tCur + vec2 (0.5 * pi, 0.));
  dMin = dstFar / heliSz;
  p -= flDisp;
  p.xz = Rot2D (p.xz, 0.25 * pi);
  p = flMat * p / heliSz;
  q = p;
  r = 1.;
  if (q.z < -0.1) {
    s = (q.z + 0.1) * (q.z + 0.1);
    r *= 1. - 0.1 * s;
    q.y -= 0.05 * s;
  } else if (q.z > 0.1) {
    s = (q.z - 0.1) * (q.z - 0.1);
    r *= 1. - 0.03 * s;
  }
  q.x *= 0.8;
  d = PrCapsDf (q, max (r, 0.), 2.);
  q = p;
  q.yz = Rot2D (q.yz - vec2 (1.1, -4.2), -0.05 * pi);
  r = 0.2 * (1. + 0.25 * q.z);
  q.y *= 0.7;
  d = min (d, PrCapsDf (q, r, 1.5));
  q = p;
  q.yz -= vec2 (1.7, -0.2);
  d = min (d, PrCylDf (q.xzy, 0.4, 0.4));
  q = p;
  q.yz -= vec2 (0.4, -1.);
  r = 1.1;
  if (q.z < 0.) {
    s = q.z * q.z;
    r *= 1. - 0.2 * s;
    q.y -= 0.1 * s;
  }
  q.y *= 0.8;
  d = SmoothMin (d, PrCapsDf (q, max (r, 0.), 1.5), 0.03);
  q = p;
  q.yz -= vec2 (1.4, -6.15);
  d = min (d, max (PrRoundCylDf (q.yzx, 0.5, 0.05, 0.02), 0.35 - length (q.yz)));
  q = p;
  DMINQ (idHeli);
  q = p;
  q.yz = Rot2Cs (q.yz - vec2 (1.4, -6.15), csRotor);
  d = PrRoundBoxDf (q, vec3 (0.01, 0.32, 0.04), 0.02);
  DMINQ (idRotorT);
  q = p;
  q.yz -= vec2 (2., -0.2);
  d = PrCylDf (q.xzy, 0.12, 0.5);
  q.y -= 0.4;
  q.xz = Rot2Cs (q.xz, csRotor);
  d = min (d, PrRoundBoxDf (q, vec3 (4.5, 0.02, 0.08), 0.03));
  DMINQ (idRotorM);
  q = p;
  q.x = abs (q.x);
  q.xy -= vec2 (0.9, -1.4);
  d = PrRoundBoxDf (q, vec3 (0.1, 0.01, 1.4), 0.04);
  q.xy = Rot2D (q.xy, -0.1 * pi);
  q.z = abs (q.z);
  q.yz -= vec2 (0.4, 0.7);
  d = min (d, PrCylDf (q.xzy, 0.06, 0.4));
  DMINQ (idSkd);
  q = p;
  q.yz -= vec2 (-0.8, 2.1);
  d = PrCapsDf (q.xzy, 0.2, 0.2);
  q.yz -= vec2 (-0.2, 0.3);
  d = min (d, PrCylDf (q, 0.1, 0.3));
  DMINQ (idGun);
  return 0.7 * heliSz * dMin;
}

void ObjState ()
{
  vec3 vd, cb, sb;
  vec2 h;
  float tc, t, tt, flPit, flYaw, flRol;
  tc = tCur + 0.7 * (gId.x + gId.y);
  t = cos (2. * pi * mod (0.6 * tc, 1.));
  tt = (mod (0.3 * tc, 6.) - 2.) / 4.;
  h = Hashv2v2 (vec2 (17.1, 19.1) * gId + 1.1) - 0.5;
  flDisp.xz = hgSize * (gId + 0.5 + 0.2 * h);
  flDisp.y = 2. + ((tt > 0.) ? 0.2 * (1. - t) + (7. + dot (h, vec2 (0.5))) *
     SmoothBump (0.2, 0.8, 0.2, tt) : 0.);
  flPit = 0.03 * pi + 0.02 * pi * sin (0.4 * pi * (h.x + h.y) * tCur);
  flYaw = 0.03 * pi * sin (0.3 * pi * h.y * tCur);
  flRol = 0.02 * pi * sin (0.2 * pi * h.x * tCur);
  cb = cos (vec3 (flPit, flYaw, flRol));
  sb = sin (vec3 (flPit, flYaw, flRol));
  flMat = mat3 (1., 0., 0., 0., cb.x, - sb.x, 0., sb.x, cb.x) *
          mat3 (cb.z, - sb.z, 0., sb.z, cb.z, 0., 0., 0., 1.) *
          mat3 (cb.y, 0., - sb.y, 0., 1., 0., sb.y , 0., cb.y);
}

float ObjRay (vec3 ro, vec3 rd)
{ // (from "Flying Bathtubs")
  vec3 p, rdi;
  vec2 gIdP, s;
  float dHit, d, eps;
  eps = 0.0005;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  rdi.xz = 1. / rd.xz;
  gIdP = vec2 (-999.);
  dHit = eps;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    gId = floor (p.xz / hgSize);
    if (gId != gIdP) {
      ObjState ();
      gIdP = gId;
    }
    d = (Maxv2 (abs (gId)) <= grLim) ? ObjDf (p) : dstFar;
    s = (hgSize * (gId + step (0., rd.xz)) - p.xz) * rdi.xz;
    d = min (d, abs (Minv2 (s)) + eps);
    dHit += d;
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  gIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    gId = floor (p.xz / hgSize);
    if (gId != gIdP) {
      ObjState ();
      gIdP = gId;
    }
    if (Maxv2 (abs (gId)) <= grLim) {
      h = ObjDf (p);
      sh = min (sh, smoothstep (0., 0.05 * d, h));
    } else h = 0.3 * hgSize;
    d += clamp (h, 0.02, 0.5);
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  q = 0.01 * (ro.xz + tCur * vec2 (0.5, 2.) + ((100. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.1, 0.8, ff);
  fd = smoothstep (0.1, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = (0.8 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  fd = smoothstep (0.01, 0.1, rd.y);
  col = mix (mix (vec3 (0.8, 0.8, 0.75), vec3 (0.4, 0.5, 0.8), 0.3 + 0.7 * fd), clCol,
     0.1 + 0.9 * f * fd);
  return col;
}

float GrndHt (vec2 p)
{  // (from "Wind Farm")
  return Fbm2 (0.4 * p) - 0.45;
}

float GrndHtN (vec2 p)
{
  float h;
  h = GrndHt (p);
  if (h > 0.) h = 0.3 * h + 0.3 * Fbm2 (2. * p);
  return h;
}

vec3 GrndNf (vec2 p)
{
  vec2 e;
  e = vec2 (0.001, 0.);
  return normalize (vec3 (GrndHtN (p) - vec2 (GrndHtN (p + e.xy), GrndHtN (p + e.yx)), e.x)).xzy;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, row;
  vec2 s;
  float dstObj, dstGrnd, sh;
  bool wtRefl;
  heliSz = 0.5;
  wtRefl = false;
  dstGrnd = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  dstObj = ObjRay (ro, rd);
  if (dstGrnd < min (dstObj, dstFar)) {
    row = ro + dstGrnd * rd;
    s = row.xz + (hVel / sqrt(2.)) * tCur;
    if (GrndHt (s) < 0.) {
      ro = row;
      row.xz = s;
      vn = VaryNf (2. * row, vec3 (0., 1., 0.), 0.05 * (1. - smoothstep (0.1, 0.4, dstGrnd / dstFar)));
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
      dstObj = ObjRay (ro, rd);
      dstGrnd = dstFar;
      wtRefl = true;
    }
  }
  if (min (dstObj, dstGrnd) < dstFar) {
    if (dstObj < dstGrnd) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      if (idObj == idHeli) {
        col4 = mix (vec4 (1., 0., 0., 0.2), vec4 (0.75, 0.85, 0.9, 0.2), smoothstep (0., 0.02,
           abs (qHit.y) - 0.04));
        if (length (vec2 (qHit.x, qHit.y / 0.8)) > 1.24 && qHit.y > 0.55 && qHit.z > 0.4 &&
           abs (abs (qHit.x) - 0.3) > 0.05) col4 = vec4 (0., 0.5, 0.5, -1.);
        if (PrRoundBox2Df (qHit.zy - vec2 (-1., 0.8), vec2 (1.2, 0.6), 0.01) < 0.) col4 =
           mix (col4, vec4 (1., 0., 0., 0.2), ShowIntPZ ((qHit.zy - vec2 (-1.2 + 0.4 * sign (qHit.x), 0.8)),
           vec2 (sign (qHit.x), 0.5), 3., 1. + grLim - gId.x + (2. * grLim + 1.) * (grLim - gId.y)));
      } else if (idObj == idRotorM) {
        col4 = (length (qHit.xz) < 4.2) ? vec4 (0.8, 0.8, 0.9, 0.1) : vec4 (1., 1., 0.2, 0.1);
      } else if (idObj == idRotorT) {
        col4 = (length (qHit.yz) > 0.06) ? vec4 (0.8, 0.8, 0.9, 0.1) : vec4 (1., 1., 0.2, 0.1);
      } else if (idObj == idSkd) {
        col4 = vec4 (0.7, 0.7, 0.75, 0.1);
      } else if (idObj == idGun) {
        col4 = vec4 (0.8, 0.8, 0.8, 0.2);
      }
    } else {
      ro += dstGrnd * rd;
      gId = floor (ro.xz / hgSize);
      s = ro.xz + (hVel / sqrt(2.)) * tCur;
      col4 = mix (vec4 (0.4, 0.5, 0.3, 0.), vec4 (0., 0.5, 0.1, 0.),
         smoothstep (0.2, 0.8, Fbm2 (4. * s)));
      col4 = mix (vec4 (0.2, 0.5, 0.2, 0.), col4,  1. - smoothstep (0.3, 0.6, dstGrnd / dstFar));
      vn = GrndNf (s);
    }
    if (col4.a >= 0.) {
      sh = (min (dstObj, dstGrnd) < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
      col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, sunDir), 0.) + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         step (0.95, sh) * col4.a * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
      col = mix (col, SkyCol (ro, rd), (wtRefl ? 0.2 + 0.8 * smoothstep (0.4, 0.6, dstObj / dstFar) :
         smoothstep (0.8, 0.9, min (dstGrnd, dstObj) / dstFar)));
    } else col = mix (col4.rgb, SkyCol (ro, reflect (rd, vn)), 0.8);
  } else col = SkyCol (ro, rd);
  col *= vec3 (1., 0.85, 0.85) * (wtRefl ? 0.85 : 1.);
  return clamp (col, 0., 1.);
}

#define AA  0   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  hgSize = 8.;
  grLim = 4.;
  hVel = 2.;
  az = pi;
  el = 0.;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  } else {
    az = pi + 2. * pi * (0.5 + abs (mod (0.01 * tCur, 2.) - 1.));
  }
  el = clamp (el, -0.2 * pi, 0.2 * pi);
  vuMat = StdVuMat (el, az);
  ro = vec3 (0., 2., -4. - 1.4 * (grLim + 1.) * hgSize * (0.9 - 0.1 * cos (4. * az)));
  ro.xz = Rot2D (ro.xz, - az) + 0.5 * hgSize;
  zmFac = 2.5;
  dstFar = 150.;
  sunDir = vuMat * normalize (vec3 (1., 1.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float DigSeg (vec2 q)
{
  q = 1. - smoothstep (vec2 (0.), vec2 (0.04, 0.07), abs (q) - vec2 (0.13, 0.5));
  return q.x * q.y;
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  vec2 vp, vm, vo;
  float d;
  int k, kk;
  vp = vec2 (0.5, 0.5);
  vm = vec2 (-0.5, 0.5);
  vo = vp - vm;
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (Minv2 (q) >= 0. && Maxv2 (q) < 1.) {
    q.x *= mxChar;
    nDig = mxChar;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = max (val, 0.) / pow (10., mxChar - idChar - 1.);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

vec2 rotateUV(vec2 uv, float rotation)
{
    float mid = 0.5;
    return vec2(
        cos(rotation) * (uv.x - mid) + sin(rotation) * (uv.y - mid) + mid,
        cos(rotation) * (uv.y - mid) - sin(rotation) * (uv.x - mid) + mid
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvd = rotateUV(uv, 1.0) + (sin(uv.x*17.0 + iTime/1.0) / (8.0 + (uv.x * iTime)/80.0));


    float waveStrength = abs(cos(uvd.x * 20.0 + iTime)) + fract(sin((uvd.x + uv.x) * 10.0));

    float highlight = 1.0 - smoothstep(0.0, 0.5, waveStrength);

    vec3 highlightCol = vec3(uvd, uv.x);

    vec3 col = vec3(0.011, 0.098, 0.866) * waveStrength + highlightCol * highlight;

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 Ball1 = vec2(0.5);
vec2 Ball2 = vec2(0.5);
vec2 Ball3 = vec2(0.5);
vec2 Ball4 = vec2(0.5);
float radio = 0.02;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Change position over time
    Ball1 = 0.5 + 0.1*cos(iTime+uv.xy+vec2(2));
    Ball2 = 0.5 + 0.2*cos(iTime+uv.xy+vec2(2));
    Ball3 = 0.5 + 0.3*cos(iTime+uv.xy+vec2(2));
    Ball4 = 0.5 + 0.4*cos(iTime+uv.xy+vec2(2));


    // Calculate distances of the pixel to the ball position
    float d1 = distance(uv, Ball1);
    float d2 = distance(uv, Ball2);
    float d3 = distance(uv, Ball3);
    float d4 = distance(uv, Ball4);

    // Sum all distances
    vec2 sum = vec2(radio/d1 + radio/d2 + radio/d3 + radio/d4);

    vec2 inf = vec2(0.3);
    // Get mouse Position
    // inf = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    vec3 col = vec3(sum.x*inf.x, sum.y*inf.y, inf.x*inf.y);
    // Output to screen
    fragColor = vec4(col,1.0);
}

// Quick demonstration on how sampling function values on a grid introduces topology errors 
// The example is with bilinear interpolation, but the same would happen with marching squares ( or marching cubes in 3d)
//  - red: a generic iso function
//  - blue (left side): bilinear interpolation 
//  - green (right side): bilinear interpolation (keeping just topology with 2^4=16 cases)
// The topology errors seem to occur when:
//   - the function crosses a square side more than once
//   - the square contains an island or an hole (ie: function not crossing any side)
//   - the square contains a manifold ( 4 intersections, one at each side);in this case errore probability is 50%

#define R iResolution.xy
float S(vec2 uv){return (texture(iChannel0,uv/6.).x);}
float T(vec2 uv){return floor(texture(iChannel0,uv/6.).x+.5);}
#define SAMPLE(fn) vec4( fn(floor(uv*z)/z), fn(floor(uv*z+vec2(1,0))/z), fn(floor(uv*z+vec2(0,1))/z), fn(floor(uv*z+vec2(1,1))/z));
#define INTERP(fn) mix(mix(fn.x,fn.y,fract(uv.x*z)),mix(fn.z,fn.w,fract(uv.x*z)),fract(uv.y*z)))

void mainImage( out vec4 O, in vec2 U )
{
    vec2 uv = U/R.y;
    float z = 25. + sin(iTime*.1)*15.;
    vec3 col=vec3(T(uv),0,0)/20./abs(.5-texture(iChannel0,uv/6.).x);
    col=mix(vec3(1),col,smoothstep(0.,.005*z, length(.5- fract(uv*z-.5))));

    vec4 f=SAMPLE(T);
    vec4 g=SAMPLE(S);

    if(uv.x>.5*R.x/R.y) col.g=(floor(.5+INTERP(f));
    else col.b=(floor(.5 +INTERP(g));

    O = vec4(col,1.0);
}

//Parameters: x, vector from which the distance is calculated
//	      r, sphere radius
//	      c, sphere center vector
//Return value: signed distance to sphere surface
float distance_to_sphere(vec3 x, float r, vec3 c) {
    return length(x-c)-r;
}

float softmin(float x, float y) {
    float K=8.0;
    return -log(exp(-K*x) + exp(-K*y))/K;
}

const float pi = 3.14159265;

//Parameters: x, vector to be rotated,
//	      axis, axis around which it is to be rotated
//	      angle, rotation angle
//Return value: rotated vector
vec3 rotate(vec3 x, vec3 axis, float angle) {
   return x*cos(angle) + cross(axis, x)*sin(angle) + axis*(1.-cos(angle))*dot(axis, x);
}

//Parameters: x, spatial position
//Return value: signed distance from x to a 3D object representing a stylized gear shape
float distance_to_gear(vec3 x) {
    //d1 is the distance to a central sphere

    float d1 = distance_to_sphere(x, 0.5, vec3(0.0, 0.0, 0.0));

    //d2 will after the loop be the distance to the union of little spheres that act as cogs

    const float n=7.0;

    float d2 = 10000000.0;
    for(float i=0.0; i < n; i = i + 1.0) {
        float d = distance_to_sphere(x, 0.1, vec3(0.6*cos(2.0*pi/n*i), 0.6*sin(2.0*pi/n*i), 0.0));
        if(d < d2)
            d2 = d;

        d = distance_to_sphere(x, 0.05, vec3(0.7*cos(2.0*pi/n*i), 0.7*sin(2.0*pi/n*i), 0.0));
        if(d < d2)
            d2 = d;
    }

    return softmin(d1,d2);
}

//Parameters: x, spatial position
//            y, sign representing the direction of the translatory motion
//Return value: signed distance to the shape swept out by the shape to which distance_to_gear defines the distance to under rotation and translation.
float distance_to_reciprocal_gear(vec3 x, float s) {
    x.x = mod(x.x, 2.0*pi/7.0); //Use translation symmetry

    float relevant_angle,
          d = 1000000.0;
     for(float i=0.0; i <= 2.0*pi/7.0; i += 0.1) {
        float temp = distance_to_gear(rotate(x+s*vec3(i, 0.0, 0.0), vec3(0,0,1), i));
        if(temp < d) {
            d = temp;
            relevant_angle = i;
        }
    }

    for(int i = 0; i != 2; ++i) {
        const float epsilon = 0.001;
        float grad = distance_to_gear(rotate(x + vec3(s*(relevant_angle + epsilon), 0.0, 0.0), vec3(0.0, 0.0, 1.0), relevant_angle + epsilon));
        grad = grad - distance_to_gear(rotate(x + vec3(s*relevant_angle, 0.0, 0.0), vec3(0.0, 0.0, 1.0), relevant_angle));
        grad = grad/epsilon;

        relevant_angle = relevant_angle - 0.8*grad;
    }

    d = distance_to_gear(rotate(x+s*vec3(relevant_angle, 0.0, 0.0), vec3(0.0, 0.0, 1.0), relevant_angle));  

    return d;
}

//Parameters: x, spatial position
//Return value: Signed distance from x to a particular cylinder
float cyl(vec3 x) {
    return length(x.yz) - 0.25;
}

//Signed distance to the xy plane with normal pointing in the -z direction
float distance_beyond_xy_plane(vec3 x) {
    return -x.z;
}

//Parameters: x, spatial position
//Return value: Signed distnace to a complete scene
//
// This scene has several characteristics:
// One thing shown in it is the gear,the distance to which is d
// We construct a rack which meshes with the gear in one rotation direction
// the distance to which is d2
// and we construct another rack which meshes with the gear in another rotation direction
// the distance to which is d5
// The intersections of these racks with two cylinders from which the front half has been
// subtracted using the 'distance_beyond_xy_plane' function.
float distance_to_scene(vec3 x) {
    float d = distance_to_gear(rotate(x, vec3(0.0, 0.0, 1.0), iTime));

    float d2 = distance_to_reciprocal_gear(x-vec3(mod(iTime, 2.0*pi),0.0,0.0), 1.0);
    float d3 = cyl(x-vec3(0.0, 0.5, 0.0));
    float d4 = distance_beyond_xy_plane(x);

    float d5 = distance_to_reciprocal_gear(x+vec3(mod(iTime, 2.0*pi),0.0,0.0), -1.0);
    float d6 = cyl(x-vec3(0.0, -0.5, 0.0));

    return min(min(max(max(d3, -d2), d4), d), max(max(d6, -d5), d4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from -1.0 to 1.0) 
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/min(iResolution.x, iResolution.y);

    // Camera: ray origin and direction
    vec3 ro = vec3(0.0, 0.0, -5.0);
    vec3 camera_direction = vec3(0.0, 0.0, 0.0) - ro;
    vec3 rd = vec3(uv.xy, 0.0)-ro;
    rd = normalize(rd);

    //Walk along the ray to get to the scene surface

    vec3 current_position;
    current_position = ro;
    for(int i = 0; i != 15; ++i)
        current_position = current_position + rd*distance_to_scene(current_position);

    //Is there a hit?

    const float epsilon = 0.005;

    float col;

    if(distance_to_scene(current_position) < epsilon) {
        float d = distance_to_scene(current_position);
        float d_plusx = distance_to_scene(current_position + epsilon*vec3(1.0,0.0,0.0));
        float d_plusy = distance_to_scene(current_position + epsilon*vec3(0.0,1.0,0.0));
        float d_plusz = distance_to_scene(current_position + epsilon*vec3(0.0,0.0,1.0));

        vec3 grad = vec3(d_plusx - d, d_plusy - d, d_plusz - d);
        grad = grad/epsilon;

        //Grad is now an outward pointing normal
        //We could do raytracing, but we're instead just going to color the scene
        //by the normal

        //Specifically, the color will be the cosine of the angle with a couple of fixed vectors
        //as is traditional

        vec3 v = vec3(1.0, 1.0, 1.0);

        float agreement = dot(v, grad)/(length(grad) * length(v));
        col = 0.00 + 0.5*max(agreement, 0.);

        //Now, to brighten things up we do a reflection

        vec3 normal = normalize(grad);

        vec3 rd_along_normal = dot(rd, normal)*normal;
        vec3 rd_orthogonal_to_normal = rd - rd_along_normal;

        //Make flipped vector
        ro = current_position;
        rd = -rd_along_normal + rd_orthogonal_to_normal;

        //Now do the marching again

        current_position = ro;
        for(int i = 0; i != 10; ++i)
               current_position = current_position + rd*distance_to_scene(current_position);

        //and what's the normal there again?

        d = distance_to_scene(current_position);
        d_plusx = distance_to_scene(current_position + epsilon*vec3(1.0,0.0,0.0));
        d_plusy = distance_to_scene(current_position + epsilon*vec3(0.0,1.0,0.0));
        d_plusz = distance_to_scene(current_position + epsilon*vec3(0.0,0.0,1.0));

        grad = vec3(d_plusx - d, d_plusy - d, d_plusz - d);
        grad = grad/epsilon;

        //get the color there and add it multiplied by something like 0.9 (or, well, it seems I picked 1.0)

        agreement = dot(v, grad)/(length(grad) * length(v));
        col = col + max(agreement, 0.0);

    } else col = 0.0;

    fragColor = vec4(col, col, col, 1.0);
}

