vec3 baseCol=vec3(0.25f,0.10f,0.0f);
vec3 detailCol = vec3(1.0f,0.60f,0.0f);
float xScale=3.0f;
float yScale=3.0f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy;
    uv=uv*2.0f-1.0f;
    uv.x*=(iResolution.x/iResolution.y);
    uv*=xScale*yScale;

    float fac=4.0f;
    float freq=1.5f;
    float t=get_octave_noise(freq*uv/(xScale*yScale));

    float p = sqrt(pow(uv.x*xScale,2.0f)+pow(uv.y*yScale,2.0f));
    p += t*fac;
    float h = sin(p);

    h=h*h;
    h=1.0f-h;
    h+=0.2f;
    h=pow(h,2.0f);
    h=smoothstep(h,-1.0f,-0.2f);

    vec3 col=mix(baseCol,detailCol*h,h);  

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define STEPS 1024
#define MIN_T 1e-4
#define MAX_T 8.0

float map(vec3 p) {
    return (length(p) - 1.0)+(sin(8.0*p.x)*sin(8.0*p.y)*sin(8.0*p.z))*0.5;
}

vec3 normal(vec3 p) {
    const float e = MIN_T;
    const vec2 h = vec2(1,-1);
    return normalize(h.xyy*map(p + h.xyy*e)+
                     h.yyx*map(p + h.yyx*e)+
                     h.yxy*map(p + h.yxy*e)+
                     h.xxx*map(p + h.xxx*e));
}

// Common technique, total distance summed by estimation.
float raymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    for(int i = 0; i < STEPS; i++) {
        float d = map(ro + rd * t);
        t += d;
        if(d < MIN_T || t > MAX_T) break;
    }
    return t;
}

