// Relaxing Ray Marcher : https://erleuchtet.org/~cupe/permanent/enhanced_sphere_tracing.pdf
float hraymarch(vec3 ro, vec3 rd) {
    float t = 0.0;
    float relax = 0.25;
    float last_d = 0.0;
    float last_dt = 0.0;
    for(int i = 0; i < STEPS; i++) {
        if(t > MAX_T) break;
        float d = map(ro + rd * t);
        if(relax != 1.0 && last_dt > abs(last_d) + abs(d)) {
            t += (1.0 - relax) * last_dt;
            relax = 1.0;
            continue;
        }
        if(d < MIN_T) break;
        t += relax * d;
        last_dt = relax * d;
        last_d = d;
    }
    return t;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x, iResolution.y);
    vec3 at = vec3(0, 0, 0);
    vec3 ro = vec3(cos(iTime*0.25)*3.0, 2, sin(iTime*0.25)*3.0);
    vec3 z = normalize(at - ro);
    vec3 x = normalize(cross(vec3(0, 1, 0), z));
    vec3 y = cross(z, x);
    vec3 rd = normalize(uv.x * x + uv.y * y + 1.0 * z);
    float t = uv.x < 0.0 ? raymarch(ro, rd) : hraymarch(ro, rd);
    vec3 p = ro + rd * t;
    vec3 nor = normal(p);
    vec3 col = t < MAX_T ? vec3(nor.y*0.5+0.5) : vec3(rd.y*0.5+0.5);

    fragColor = vec4(sqrt(col)*smoothstep(0.0005, 0.005, abs(uv.x)), 1.0);
}

