const float M_PI = 3.14159265358979323846264338327950288;

const vec3 WHITE_STAR_COLOR = vec3(1.);
const vec3 BLUE_STAR_COLOR = vec3(0.35294117647,0.42352941176,0.67058823529);
const vec3 RED_STAR_COLOR = vec3(0.72549019607,0.05098039215,0.30980392156);
const vec3 BACKGROUND_BLACK = vec3(0.01176470588);
const vec3 MOUNTAIN_COLOR = vec3(0.0862745098,0.1725490196,0.30588235294);

const float STAR_MAX_SIZE = 0.2;
const float STAR_MIN_SIZE = 0.05;

float remap01(float a, float b, float t)
{
    return clamp01((t-a)/(b-a));
}

float Rand(float i)
{
    return fract(sin(i * 23325.) * 35543.);
}

vec4 Rand4(float i)
{
    return fract(sin(i * vec4(23325.,53464.,76543.,12312)) * vec4(35543.,63454.,23454.,87651));
}

vec2 within(vec2 uv, vec4 rect)
{
    return (uv - rect.xy)/(rect.zw-rect.xy);
}

float Star(vec2 uv, float radius, float iradius, float rotation)
{
    vec2 st = vec2 (atan(uv.x,uv.y), length(uv));

    float n = 5.;

    float a  = st.x + rotation;
    float sa = M_PI/n;

    float p = fract(a/(2.0*sa));
          p = abs(p-0.5)*2.0;

    float cr = cos(sa)*radius;

    float pa  = p*sa;
    float cpr = cr/cos(sa-pa);

    float xp = cpr*sin(pa);
    float yp = cpr*cos(pa);
    float xi = sin(sa*0.5)*iradius*radius;
    float yi = cos(sa*0.5)*iradius*radius;

    float yx = (xi*yp*radius)/(xp*radius-xp*yi+xi*yp);

    float border = yx/cos(pa);

    return smoothstep(0.,0.1,1. -st.y/border);
}

vec3 ThreeStar(vec2 uv, float rotation){

    float radius  = 0.5;
    float iradius = .5;  

    float redStar   = Star(uv - vec2(.055,-.09), radius, iradius, rotation);
    float blueStar  = Star(uv - vec2(.0,.0), radius, iradius, rotation);

    vec3 whiteStarColor = blueStar * redStar * WHITE_STAR_COLOR;
    vec3 redStarColor   = redStar * RED_STAR_COLOR;
    vec3 blueStarColor  = blueStar * BLUE_STAR_COLOR;

    return whiteStarColor + redStarColor + blueStarColor;
}

vec3 StarSky(vec2 uv){

    float t = iTime*0.025;
    float sr = iResolution.x/iResolution.y;

    vec3 color = vec3(0.);

    float sc = 15.;
    float s = 1./ sc;
    float at = 0.01;

    float stt = 0.1;
    vec2 spading = vec2(0.95,0.9);

    for(float i = 0.; i < 1.; i +=s)
    {    
        float ci = t + i;
        float fci = fract(ci);
        float sit = floor(ci);

        float btout = pow(smoothstep(0.9,1.0,fci),20.);        
        float ibtout = 1.-btout;

        float bt = ci * 500.;
        float h1 = Rand(floor(bt));
        float h2 = Rand(floor(bt+1.));
        float bumps = mix(h1, h2, fract(bt))*.1;
        bumps = pow(bumps,3.) * 10.;
        float shake = smoothstep(0.9,1.0,fci) * bumps;

        float btin = pow(smoothstep(0.,0.1,fci),2.);

        vec4 rm = Rand4(i + sit);

        float rs = STAR_MIN_SIZE + rm.x * (STAR_MAX_SIZE-STAR_MIN_SIZE) * ibtout;
        vec2 rp  = vec2((rm.y*2. - 1.)*sr + shake ,rm.z + shake) * spading;

        rp  = (vec2(sr * 0.9,-0.5) - rp) * btout + rp;

        vec4 rb  = vec4(rp.x, rp.y, rp.x + rs, rp.y + rs);
        vec2 st  = within(uv, rb);

        vec3 ts = ThreeStar(st,M_PI);
        ts *= ibtout;
        ts *= btin;
        color += ts;

    }

    return color;
}

vec3 Background(vec2 uv)
{ 
    vec3 dc = BACKGROUND_BLACK;
    vec3 bc = BLUE_STAR_COLOR;
    vec3 rc = RED_STAR_COLOR;
    vec3 wc = WHITE_STAR_COLOR;

    vec3 color = mix (bc, dc, smoothstep(-0.8,-0.1,uv.y));
    color = mix (rc, color, smoothstep(-1.0,-0.55,uv.y));
    color = mix (wc, color, smoothstep(-1.4,-0.7,uv.y));
    return color;
}

vec3 MountainsOverlay(vec2 uv, vec3 color, float mountainLevel)
{
    float pos = uv.x + iTime * 0.05;
    float bt = pos * 5.;
    float h1 = Rand(floor(bt));
    float h2 = Rand(floor(bt+1.));
    float bumps = mix(h1, h2, fract(bt))*.1;
    bumps = pow(bumps,3.) * 300.;

    mountainLevel += bumps;

    float mask = 1.-smoothstep(mountainLevel,mountainLevel*0.99, uv.y);
    vec3 mc =  smoothstep(-1.2, mountainLevel+0.1, uv.y) * MOUNTAIN_COLOR;
    color = mix(color,mc, mask);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    vec3 color = StarSky(uv) + Background(uv);
    color = MountainsOverlay(uv,color,-0.9);

    fragColor = vec4(color,1.0);
}

