//------------------------------------------------------------------
float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}
//------------------------------------------------------------------

float map( in vec3 pos )
{
    vec3 qos=pos;
    float d=1e9;
    d=min(d,sdPlane(qos));
    qos.xz=mod(qos.xz,1.0)-vec2(0.5);
    d=mod(iTime,6.0)<3.0?min(d,sdSphere(qos-vec3(0.0,0.5,0.0),0.45)):d;
    return d;
}

int mateId( in vec3 pos )
{
    int id;
    float d=1e10,b;
    vec3 qos=pos;

    b=sdPlane(qos);
    if(d>b){
        d=b;
        vec2 uv=pos.xz;
        id=mod(uv.x*uv.y,2.0)<1.0?1:2;
    }
    qos.xz=mod(qos.xz,1.0)-vec2(0.5);
    b=sdSphere(qos-vec3(0.0,0.5,0.0),0.45);
    if(d>b){
        d=b;
        id=0;
    }
    return id;
}

vec3[] mateCol=vec3[](
    vec3(0.5,0.8,1.0)*0.8,
    vec3(0.2,0.3,1.0)*0.8,
    vec3(0.2,0.8,0.7)
);

//------------------------------------------------------------------

mat2 rot2(float a){
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

//------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax)
{
	float res = 1.0;
    float t = mint;
    float ph = 1e10;

    for( int i=0; i<32; i++ )
    {
		float h = map( ro + rd*t );

        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, 10.0*d/max(0.0,t-y) );
        ph = h;

        t += h;

        if( res<0.0001 || t>tmax ) break;

    }
    res = clamp( res, 0.0, 1.0 );
    return res*res*(3.0-2.0*res);
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ) + 
					  e.yyx*map( pos + e.yyx ) + 
					  e.yxy*map( pos + e.yxy ) + 
					  e.xxx*map( pos + e.xxx ) );
}

float castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 25.0;
    float ymin = -1.0;
    float ymax = 3.0;

    float tp1 = (ymin-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (ymax-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>ymax ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }

    float t = tmin;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0005*t;
	    float res = map( ro+rd*t );
        if( res<precis || t>tmax ) break;
        t += res;
    }

    if( t>tmax ) t=-1.0;
    return t;
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.001 + 0.15*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );    
}

vec3 skybox(vec3 rd)
{
    //return texture(iChannel0,rd).rgb;
    return vec3(0.1);
}

vec3 render( in vec3 ro, in vec3 rd)
{ 
    /// default is skybox
    vec3 col=skybox(rd);
    //vec3 col=vec3(0.0);
    float t = castRay(ro,rd);

    /// ray collide
    if( t>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );

        int id=mateId(pos);

        /// inout: ro,rd,t,pos,nor,id
        /// refract process
        int rdepth=2;
        float eta=0.5;
        while(rdepth-->0)
        {
            if(id==0)
            {
                /// reflect
                ro=pos;
                rd=refract(rd,nor,eta);
                t=castRay(ro,rd);
                if(t>-0.5)
                {
                    /// next reflect
                    pos = ro + t*rd;
                    nor = calcNormal( pos );
                    id=mateId(pos);
                }else
                {
                    /// ray far away
                    return skybox(rd);
                }
            }else
            {
                /// not reflect
                break;
            }
        }

        /// set material
        vec3 mate=mateCol[id];

        /// calc lighting
        vec3  lig = normalize( vec3(-0.1, 0.3, 0.6) );
        vec3  hal = normalize( lig-rd );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 ) * 
                    calcSoftshadow( pos, lig, 0.01, 3.0);

		float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

		col = mate * 4.0*dif*vec3(0.50,0.80,0.80);
        col +=      12.0*spe*vec3(1.00,0.80,0.50);

        float occ = calcAO( pos, nor );
        float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        col += mate*amb*occ*vec3(0.0,0.08,0.1);

        col *= exp( -0.0005*t*t*t );//fog
    }

	return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /// camera
    float arg=2.0*PI*(iTime/20.0);
    vec3 ro = vec3( 3.0, 5.0, 3.0 );ro.xz*=rot2(arg);
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    /// camera-to-world transformation
    mat3 ca = setCamera( ro, ta ,0.0);

    vec3 scol=vec3(0);

    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        //vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

        /// ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        /// render
        vec3 col = render( ro, rd);

        /// gamma
        col = pow( col, vec3(0.4545) );

        //sum
        scol+=col;
    }
    scol/=float(AA*AA);
    fragColor = vec4( scol, 1.0 );
}

