    float sr = 5.;

    float a = iTime*.05 * (max(-1.0,cos(iTime*.25))* .005+.995 );

    p = vec3(sr*cos(a)-.85,sr*sin(a), 0.2);
    vec3 d = normalize(vec3(pc,1.0));
    d.yz *= rotate(1.341592);
    d.xy *= rotate(-a+4.63);

    vec3 c=vec3(0.0);
    float t = 0.;
    float pd = 1e19;
    vec4 hit;
    vec4 hitn;
    vec3 cadd;
    vec3 cadd2;
    float caddm = 1.; 
    for (int i = 0 ; i < 200; i++)
    {
        hit = dist(p);           // current 
        hitn = dist(p+ d*hit.x); // next 
        float h=hit.x; 
        float h2=hitn.x;
        h = (h+h2)/2.0;

        //float y = h*h/(2.0*pd);
        //float k = sqrt(h*h-y*y);
        //pd = h;
        //h = min(h, h/max(0.0, t-y));

        if (h < 0.01)
         break;
        t+=h;
        p += d*h;
        cadd += vec3(1,.5,.25)*h;

        caddm = min(caddm, h);
        cadd2 += vec3(1,.5,.25)/caddm;;
    }
    vec3 pn = getNormal(p);
    c += pn.z*.0;



    if (hit.y==0.0) {
    }
    if (hit.y==2.0) {
        c = cadd2*.5 ;
    } if (hit.y==1.0) {
        c = (pn.z*.5+.5)*(hsv2rgb(p.x+p.y+p.z)*.5+.75  ) ; 
    }
    else
        c += vec3(abs(pn.z)) *(cadd2)/(t*190.); 


    c = mix(c, c * (1.-(log(cadd))*1.0), max(0., sin(-iTime*.25)));


    fragColor = vec4(c,1.0);
}

