vec2 triangle_wave(vec2 a,float scale)
{    return abs(fract((a+c1.xy)*scale)-.5); }

vec2 triangle_wavePC(vec2 a,float scale, vec4 Pc)
{  return abs(fract((a+Pc.xy)*scale)-.5);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
