    float t2 =tfijo;

    vec3 random1 = (hash31(3.+t2)-.5)/12.;
    vec3 random2 = (hash31(4.+t2)-.5)/12.;

    float offset = .5;
    float scale2 = 1.5;

    vec2 va1;
    vec2 va2;

    for(int c=0;c<3;c++){
        float scale = c1.z;
        for(int i=0;i<3;i++)
        {
            for(int k = 0; k < 3; k++)
            {
                uv /= -scale2;
                vec2 tmp1=uv.yx-offset;
                vec2 tmp2=uv;
                vec2 tv2= triangle_wavePC(tmp2,scale, cA3);
                uv.yx=tv2;
                va1=vec2(random1[k]*random1[0],random2[k]);
                va2=vec2(random1[k],random2[k]);   
                uv +=mix( va1, va2, sin(ti));
            }

            scale /= 1.+(scale2)*col.x/(8.);
