float noise(vec2 p) {
    vec2 f = fract(p);
    f = f * f * f * f * (3. - 2. * f) * (3. - 2. * f);
    vec2 i = floor(p);
    float r1 = rand(i);
    float r2 = rand(i + vec2(1, 0));
    float r3 = rand(i + vec2(0, 1));
    float r4 = rand(i + vec2(1, 1));
    return mix(r1, r2, f.x) + mix((r3 - r1) * f.y, (r4 - r2) * f.y, f.x);
}

