float box(vec2 position, vec2 halfSize, float chamferRadius) {
   position = abs(position) - halfSize;
   vec2 d1 = vec2(max(position.x + chamferRadius, 0.0), position.y);
   vec2 d2 = vec2(position.x, max(position.y + chamferRadius, 0.0));
   float k = clamp((position.x - position.y + chamferRadius) * 0.5, 0.0, chamferRadius);
   vec2 d3 = position + vec2(chamferRadius - k, k);
   float s = sign(max(d3.x, d1.y));
   return sqrt(min(min(dot(d1, d1), dot(d2, d2)), dot(d3, d3))) * s;
}

float box(vec2 position, vec2 halfSize, vec2 chamferRadius) {
   position = abs(position) - halfSize;
   vec2 d1 = vec2(max(position.x + chamferRadius.x, 0.0), position.y);
   vec2 d2 = vec2(position.x, max(position.y + chamferRadius.y, 0.0));
   position.x += chamferRadius.x; 
   vec2 end = vec2(chamferRadius.x, -chamferRadius.y);
   vec2 d3 = position - end * clamp(dot(position, end) / dot(end, end), 0.0, 1.0);
   float s = sign(max(d3.x, d1.y));
   return sqrt(min(min(dot(d1, d1), dot(d2, d2)), dot(d3, d3))) * s;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position = (position * 2.0) - 1.0;
    position.x *= iResolution.x / iResolution.y;

    vec2 halfSize = vec2(0.55);
    float chamferRadius = ((cos(iTime + 1.0) + 1.0) * 0.5) * 0.55 / (sqrt(2.0) + 1.0) * sqrt(2.0);
    float d = box(position, halfSize, chamferRadius);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7); // base color
    color *= 1.0 - exp(-4.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(120.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d)));
    if (iMouse.z > 0.001) {
        vec2 mousePosition = iMouse.xy / iResolution.xy;
        mousePosition = (mousePosition * 2.0) - 1.0;
        mousePosition.x *= iResolution.x / iResolution.y;
        float d = box(mousePosition, halfSize, chamferRadius);
        color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 0.0025, abs(length(position - mousePosition) - abs(d)) - 0.0025));
    }
    fragColor = vec4(color, 1.0);
}

