//=======================================================================================================================
//
// Rendering an ellipse implicitly with anti-aliasing using an approximation of signed distance
//
// note: This does not work well for highly eccentric (flat) ellipses.
//       However, this does not require any textures, root-finding, or conditionals.
//       What you see is what you get.
//
// Ellipse representation
//
//   names are based on conventional ellipse values A, B, and C where:
//     A is the length of the semi-major axis
//     B is the length of the semi-minor axis
//     C is the distance from the center of the ellipse to either of the foci
//     these follow a Pythagorean triple, A^2 = B^2 + C^2
//
//   EllipseCenter: vector from center of coordinate space to center of the ellipse
//                  you can easily write your vertex shader such that this is always (0, 0)
//
//   EllipseCVec:   vector from center of ellipse to one of the foci
//                  i.e. one focus is at (EllipseCenter + EllipseCVec), the other is at (EllipseCenter - EllipseCVec)
//                  this determines the orientation of the ellipse, and partially defines its eccentricity
//
//   EllipseB:      length of the ellipse's semi-minor axis
//
//=======================================================================================================================

float ApproxDistanceToEllipse(vec2 QueryPoint, vec2 EllipseCenter, vec2 EllipseCVec, float EllipseB)
{
    QueryPoint = QueryPoint - EllipseCenter;

    float EllipseC = length(EllipseCVec);
    float EllipseA = sqrt(EllipseC*EllipseC + EllipseB*EllipseB);

    float QueryA = (length(QueryPoint + EllipseCVec) + length(QueryPoint - EllipseCVec)) / 2.f;
    float QueryB = sqrt(QueryA*QueryA - EllipseC*EllipseC);

    float CosTheta = dot(QueryPoint, normalize(EllipseCVec)) / QueryA;

    // I'm not sure which of these is better for anti-aliasing purposes.
    // Maybe there's actually a better interpolation than Lerp, and we should use neither or both.
    // Cosine^2 is clearly better (just looking at it) as distance from ellipse increases, which is exactly the cases we don't care about for anti-aliasing.
    // I'm too lazy right now to implement the exact distance function and collect error metrics of both methods.
    // If anyone reading this feels like doing that for some reason, 
    // you can find David Eberly's exact distance method at https://www.geometrictools.com/Documentation/DistancePointEllipseEllipsoid.pdf section 2.9.
    // tl;dr just because the first option for LerpValue LOOKS better, doesn't mean that it is, but it might be, I haven't checked
    float LerpValue = CosTheta * CosTheta;
    //float LerpValue = abs(CosTheta);

    float Distanceish = mix(QueryB - EllipseB, QueryA - EllipseA, LerpValue);

    return Distanceish;

}

void mainImage(out vec4 FragColor, in vec2 FragCoord )
{
    // turn off to see actual anti-aliasing
    // turn on to see contours of approximated distance
    bool ShowContours = bool(0);

    // FragCoord -> range [-iResolution / 2, iResolution / 2]
    vec2 P = FragCoord - (iResolution.xy * 0.5f);

    // setup ellipse params
    float pi = radians(180.f);
    float CAngle = mod(iTime / 1.5f, 2.f*pi);
    float CMag = mix(100.f, 200.f, abs(mod(iTime / 2.3f, 2.f) - 1.f));

    vec2  EllipseCVec = CMag * vec2(cos(CAngle), sin(CAngle));
    float EllipseB = 50.f;
    vec2  EllipseCenter = vec2(100.f*cos(iTime), 50.f*sin(iTime * 1.4));

    // stationary ellipse where major axis is X axis
    if (ShowContours)
    {
        EllipseCVec = vec2(200.f, 0.f);
        EllipseB = 50.f;
        EllipseCenter = vec2(0.f, 0.f);
    }

    // get approximate distance in pixels
    float Distanceish = ApproxDistanceToEllipse(P, EllipseCenter, EllipseCVec, EllipseB);

    // do antialiasing using our approximate distance
    float Antialias = 1.5;
    float AntialiasMin = -(Antialias/2.f), AntialiasMax = Antialias/2.f;
    float Alpha = 1.f - smoothstep(AntialiasMin, AntialiasMax, Distanceish);

    // in actual use case, just use whatever texture or color you want for the ellipse
    // and use the value of 'Alpha' for the alpha channel of FragColor
    vec3 ColorOutsideEllipse = vec3(0,0.5,0.5);
    vec3 ColorInsideEllipse = vec3(0,0.2,0.2);
    vec3 Color = mix(ColorOutsideEllipse, ColorInsideEllipse, Alpha);
    FragColor = vec4(Color, 1.f);

    // visualize distance contours
    // contour coloring concept from https://www.shadertoy.com/view/tlSGzG
    if (ShowContours)
    {
        float D = Distanceish/6.f;
        float Darkness = 0.3;
        vec3 ContourColor = vec3(1.0) - sign(D)*ColorInsideEllipse;
        ContourColor *= 1.0 - exp(-2.0*abs(D));
        ContourColor *= (1.f - Darkness) + Darkness*cos(D);
        FragColor = vec4(ContourColor, 1.f);
    }
}

