const float pi = 3.1415926;
const float pi2 = 2.*pi;
const float rad = pi/180.;

mat2 rotate(float x){
    float c = cos(x);
    float s = sin(x);
    return mat2(c,s,-s,c);
}

float sdBox( vec3 p, vec3 b ) // https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

struct march{
     float x;
     vec3 p;
     float id;
};

march mac(float x){
    march m;
    m.x = x;
    return m;
}

march mac(float x, vec3 p){
    march m;
    m.x = x;
    m.p = p;
    return m;
}

march mac(float x, vec3 p, float id){
    march m;
    m.x = x;
    m.p = p;
    m.id = id;
    return m;
}

march vmin(march d, march v){
    if (d.x > v.x)
        return v;
    else 
        return d;

}

bool renderFloor = true;
march dist(vec3 p)
{
    march d = mac( 1.0 );

    d = vmin(d, mac(-p.z+7., p, 1.0));

    if (renderFloor){
      vec3 p1 = p;
      p1.yz *= rotate(-.15);
      p1.yz *= rotate(cos((p1.x+p1.z+cos(p1.y+p.z))*2.+iTime)*.04);
      d = vmin(d, mac(sdBox(p1-vec3(0.0,-2.,0.), vec3(4.0,0.1,4.)),p,1.0));
    }

    p.z+=-2.;
    p.y+=cos(iTime*1.5)-1.;
    p.x+=cos(iTime);
    p.xz *= rotate(iTime);
    p.yz *= rotate(iTime*.3);    
    d = vmin(d, mac(sdBox(p, vec3(1.0)),p));

    return d;
}

vec3 getNormal(vec3 p)
{
    vec2 d = vec2(0., 0.01);
    float x = dist(p-d.yxx).x;
    float y = dist(p-d.xyx).x;
    float z = dist(p-d.xxy).x;
    return normalize(vec3(x,y,z)-dist(p).x);
}

struct raymarch{
    float t; // distance
    vec3 p;  // position
    vec3 d;  // direction
    march h; // hit
    vec3 c;  // inv glow
    vec3 b;  // glow
    float m; // min hit
    vec3 pn; // normal
    vec3 dr; // reflect
    vec3 pd; // penultimate hit
    float len;
};

raymarch raymarching(vec3 p1, vec3 d1, float len, float maxd, float mind){
    raymarch r;
    r.t = 0.;
    r.p = p1 ;
    r.d = d1;
    r.h;
    r.c *= 0.0;
    r.m  = 1.0;
    int lenb = int(len);
    for (int i = 0 ; i < lenb; i++)
    {
        r.h  = dist(r.p );
        float h = r.h.x;
        h = min(h, mind);
        h = max(h, maxd);
        //if (h < 0.01)
        //  break;
        r.t+=h;
        r.p += r.d*h;
        r.m = min(r.m, h);
        r.c += r.m;
        //r.b += 1./(sqrt(r.m));
    }
    r.c /= (len);  
    r.b =  vec3(clamp(1.0-r.c*5.5,0.,1.));
    r.len = len;
    r.pn = getNormal(r.p); 
    r.dr = reflect( r.d, getNormal(r.p) ); 
    r.pd = r.p - r.d;
    return r;
}

vec3 getVideoColor(raymarch r)
{
    return clamp(texture(iChannel0, ( (r.h.p.xy+vec2(r.h.p.z, 0.0))*vec2(0.25,.5)+vec2(0.5,.5)) ).rgb*vec3(r.pn.z),0.0,1.0);
}

float getFloorColor(raymarch r){

    return clamp( step(.9, (r.h.id) ) + step(.9, abs(r.h.p.y) ), 0., 1. );

}

void background(vec2 uv, inout vec3 c){
    if (iTime < 20.0)
        c += texture(iChannel0, mod(uv*10.0,1.0)).rgb*.5;
    else
        c += texture(iChannel0, uv).rgb*.5;

    vec2 uvid = (floor(uv*10.0));
    float index = mod(10.-uvid.y, 10.0)+ uvid.x/10.;
    vec2 uvr = (mod(uv*10.0,1.0));
    c *= smoothstep(0.,.1, uvr.x) ;     
    c *= smoothstep(0.,.1, uvr.y) ;  
    c *= smoothstep(-0.,1.,pow(iTime*5.+11.,1.0)-length(uvr-.5)-index*10.0)+.1 ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pc = (uv-.5)*vec2(1, iResolution.y/iResolution.x);
    vec3 p1 = vec3(0,0.,-8);
    vec3 d1 = normalize(vec3(pc,1.0));

    raymarch rFirst   = raymarching( p1, d1, 70. , .0, 1.);

    raymarch rShadow  = raymarching( rFirst.pd, vec3(0,1,0), 10., 0.0, 1. );

    raymarch rReflect = raymarching( rFirst.pd, rFirst.dr, 10., 0.0, 1. );

    renderFloor = false;
    raymarch rGlow  = raymarching( p1, d1, 70. , .0, .5);

    vec3 c = getVideoColor(rFirst);                                      // ambient

    c = mix(c, vec3(0.3,0.7,rFirst.pn.z), getFloorColor(rFirst));        // color floor

    c += getVideoColor(rReflect);                                        // reflect

    c = c * rShadow.c+.25;                                               // shadow

    c += pow(clamp(+1.-rFirst.pn.z, 0., 1.0), 20.0);                     // specular

    if (rFirst.t>15.0) background(uv, c);                                // background

    c = mix(c, vec3(0.0), smoothstep(0.0, 1.0, -.3+length(uv-.5))+.2);   // fade

    c += vec3(rGlow.b);                                                  // glow 

    c += vec3(clamp(.9+pow(cos(iTime),1.0)*1.0-rGlow.c*8.,0.,1.));       // glow extra

    fragColor = vec4(c,1.0);
}

