  vec4 pos=dot(ip,st) + vec4(0,st.y,st.z,st.y+st.z);
  vec4 val=mix(fract(sin(pos)*7894.552), fract(sin(pos+st.x)*7894.552), p.x);
  vec2 val2=mix(val.xz,val.yw, p.y);
  return mix(val2.x,val2.y, p.z);
}

