float shadow(vec3 p, vec3 l, float maxdist, int stepcount, float limit) {
  float shad=1.0;
  float dd=0.0;
  for(int i=0; i<stepcount; ++i) {
    float d=min(map(p), maxdist);
    if(d<limit) {
      shad=0.0;
      break;
    }
    if(dd>=maxdist) {
      break;
    }

    p += l*d;
    dd+= d;

  }
  return shad;
}

