  }
  if ( matter == 2) {
      col = vec3(1,1,0);
  }
    if ( matter == 3) {
      col = vec3(1);
  }

 //col += probe(r, uv, dd) * .9;
 col += glow * .6;
 col = mix(vec3(.5), col, 1. - z/260.);



  // sky
  if (! hit ) {


    float per = dot(r, normalize(vec3(0,2,2)))  * .5 + .5;
    per = pow(per,2.);

    col =   mix(  vec3(.5), vec3 (.5) *.1, per);

  }

  fragColor = vec4(col, 1);

}





