vec4 mod289(vec4 x)
{
	return x - floor(x / 289.0) * 289.0;
}
vec4 permute(vec4 x)
{
	return mod289(((x * 34.0) + 1.0) * x);
}
vec4 taylorInvSqrt(vec4 r)
{
	return vec4(1.79284291400159 - r * 0.85373472095314);
}
vec2 fade(vec2 t) {
	return t * t* t* (t * (t * 6.0 - 15.0) + 10.0);
}


// Classic Perlin noise
float cnoise(vec2 P)
{
	vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
	vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
	Pi = mod289(Pi); // To avoid truncation effects in permutation
	vec4 ix = Pi.xzxz;
	vec4 iy = Pi.yyww;
	vec4 fx = Pf.xzxz;
	vec4 fy = Pf.yyww;
	vec4 i = permute(permute(ix) + iy);
	vec4 gx = fract(i / 41.0) * 2.0 - 1.0;
	vec4 gy = abs(gx) - 0.5;
	vec4 tx = floor(gx + 0.5);
	gx = gx - tx;
	vec2 g00 = vec2(gx.x, gy.x);
	vec2 g10 = vec2(gx.y, gy.y);
	vec2 g01 = vec2(gx.z, gy.z);
	vec2 g11 = vec2(gx.w, gy.w);
	vec4 norm = taylorInvSqrt(vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11)));
	g00 *= norm.x;
	g01 *= norm.y;
	g10 *= norm.z;
	g11 *= norm.w;
	float n00 = dot(g00, vec2(fx.x, fy.x));
	float n10 = dot(g10, vec2(fx.y, fy.y));
	float n01 = dot(g01, vec2(fx.z, fy.z));
	float n11 = dot(g11, vec2(fx.w, fy.w));
	vec2 fade_xy = fade(Pf.xy);
	vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
	float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
	return 2.3 * n_xy;
}
// Classic Perlin noise, periodic variant
float pnoise(vec2 P, vec2 rep)
{
	vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
	vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
	Pi = mod(Pi, rep.xyxy); // To create noise with explicit period
	Pi = mod289(Pi);        // To avoid truncation effects in permutation
	vec4 ix = Pi.xzxz;
	vec4 iy = Pi.yyww;
	vec4 fx = Pf.xzxz;
	vec4 fy = Pf.yyww;
	vec4 i = permute(permute(ix) + iy);
	vec4 gx = fract(i / 41.0) * 2.0 - 1.0;
	vec4 gy = abs(gx) - 0.5;
	vec4 tx = floor(gx + 0.5);
	gx = gx - tx;
	vec2 g00 = vec2(gx.x, gy.x);
	vec2 g10 = vec2(gx.y, gy.y);
	vec2 g01 = vec2(gx.z, gy.z);
	vec2 g11 = vec2(gx.w, gy.w);
	vec4 norm = taylorInvSqrt(vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11)));
	g00 *= norm.x;
	g01 *= norm.y;
	g10 *= norm.z;
	g11 *= norm.w;
	float n00 = dot(g00, vec2(fx.x, fy.x));
	float n10 = dot(g10, vec2(fx.y, fy.y));
	float n01 = dot(g01, vec2(fx.z, fy.z));
	float n11 = dot(g11, vec2(fx.w, fy.w));
	vec2 fade_xy = fade(Pf.xy);
	vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
	float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
	return 2.3 * n_xy;
}



 float Speed = 0.33;
 float Gradient_Adjust = 0.85;
 float Dissolve_Value = 1.43;
 bool Animated;
 bool Apply_to_Channel;
 bool Apply_Smoke = true;
 float Smoke_Horizonal_Speed = 0.3;
 float Smoke_Vertical_Speed = 0.17;
 int Iterations = 4;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0,uv);

    vec4 color = fragColor;//image.Sample(textureSampler, v_in.uv);
	vec4 smoke = vec4(1.0,1.0,1.0,1.0);
	vec4 result = smoke;
	float t = iTime * Speed;
	float cycle = 1. - max((sin(t) * 2.) - 1., 0.); //create a negative cycle time as a delay
	vec2 dir = vec2(Smoke_Horizonal_Speed, Smoke_Vertical_Speed);
	//float largestDistance = sqrt(pow(uv_size.x, 2) + pow(uv_size.y, 2));

	float perlin = 0.5;
	float smoke_perlin = 0.;
	float scale = 1.;
	float w = 0.5;	

	for (int i = 0; i < Iterations; i++) {
		//vec2 coord = v_in.uv * scale;// (v_in.uv + t * dir)* scale;
		vec2 coord = (uv + t * (dir * .1)) * scale;
		vec2 period = scale * dir;
		perlin += pnoise(coord, period) * w;
		if (Apply_Smoke)
			smoke_perlin += cnoise((uv + t * dir) * scale) * w * .5;

		scale *= 2.0;
		w *= 0.5;
	}

	//float toPoint = abs(length(v_in.uv - (v_in.uv * .5)) / ((1.0001 - t) * largestDistance));
	if (!Animated)
		cycle = 1.;
	float d = clamp(((Dissolve_Value * cycle + perlin) ) - 1.0, -.01, 0.99);
	float overOne = clamp(d * Gradient_Adjust,0.,1.);
	vec4 burn = texture(iChannel1, vec2(overOne, 0.5));

	if (Apply_to_Channel) {
		result =  color * burn;
	}
	else {
		result = vec4(perlin, perlin, perlin, 1.0) * burn;
	}	

	if (smoke_perlin > 0.) {
		smoke *= smoke_perlin;
		if (result.a <= 0.04)
			result = vec4(smoke.rgb, smoke_perlin);
		result += smoke;
	}

	fragColor= result;


}

float fovzoom = .5f;
float radius = .6f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    //Calculate render parameters
    float shake = .4*(1.f-abs(sin(iTime*.1)))+.01;
    vec3 camPos = vec3(0,sin(iTime*50.f)*shake*.1,-1.38f-shake*.75),
    pivot = vec3(.7+cos(iTime*40.f+.2)*shake*.2,cos(iTime*12.f)*shake,0),
    u = normalize(pivot-camPos),
    r = vec3(0,1,0),
    f = cross(u,r),
    sCenter = camPos+f*fovzoom,
    screenPoint = sCenter + uv.x * r + uv.y * u,
    rayDir = normalize(screenPoint-camPos);

    //Raymarch for the interior of a torus
    vec3 ray;
    float rayL, rayStep;   
    for (int i=0; i<50; ++i){
        ray = camPos + rayDir * rayL;
        rayStep = -(length(vec2(length(ray.xz)-1.1f, ray.y)) - radius);
        if(rayStep<.001) break;
        rayL += rayStep;
    }

    //Calculate torus UVs
    vec3 col = vec3(0);
    vec2 tUV = vec2(0);
    if(rayStep<.001){
        tUV.x = sin(atan(ray.x,ray.z*10.f)+iTime)+1.f;
        tUV.y = sin(atan(length(ray.xz)-1.f, ray.y)+iTime)+1.f; 
    } 

   //Calculate rainbow stripes
   vec3 mask = vec3(sin(tUV.y*30.f+iTime)*.5+.5) * 
   vec3(sin(tUV.y*9.f-iTime*0.8)*.5+.5) *
   vec3(sin(tUV.y*17.f-iTime*0.4)*.5+.5);
   col = 0.5 + 0.5*cos(iTime+tUV.xyx+vec3(0,2,4));    

    // Output to screen
    fragColor = vec4(texture(iChannel0,tUV).xyz+mask*col,1.0);
}

// rotation matrices
mat3 rotateX(float theta){
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1.,0.,0.),
        vec3(0.,c,-s),
        vec3(0.,s,c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

// identity matrix
mat3 identity() {
    return mat3(
        vec3(1, 0, 0),
        vec3(0, 1, 0),
        vec3(0, 0, 1)
    );
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float PRECISION = 0.001;

struct Surface {
 float sd;
 vec3 col;
};

Surface sdBox(vec3 p, vec3 b, vec3 offset, vec3 col, mat3 transform){
    p = (p - offset) * transform - vec3(3.,0.,0.);
    vec3 q = abs(p) - b;
    float d = length(max(q, 0.)) + min(max(q.x, max(q.y,q.z)), 0.);
    return Surface(d, col);
}

Surface sdFloor(vec3 p, vec3 col){
    float d = p.y + 1.;
    return Surface(d, col);
}

Surface minWithColor(Surface obj1, Surface obj2){
    if (obj2.sd < obj1.sd) return obj2;
    return obj1;
}

Surface sdScene(vec3 p){
    vec3 floorColor = vec3(1. + 0.7*mod(floor(p.x) + floor(p.z), 2.0));
    Surface co = sdFloor(p, floorColor);
    co = minWithColor(co,sdBox(
        p,
        vec3(.5),
        vec3(0.,.5,-4.),
        vec3(1.,0.,0.),
        rotateY(iTime)
    ));
    co = minWithColor(co,sdBox(
        p,
        vec3(.25),
        vec3(0.,1.5,-4.),
        vec3(.5,.8,.2),
        rotateY(-iTime) * rotateX(iTime*0.5)
    ));
    co = minWithColor(co,sdBox(
        p,
        vec3(.25,.1,.2),
        vec3(0.,.25,-4.),
        vec3(.1,.5,.8),
        rotateY(-iTime*0.3) * rotateZ(iTime*0.75)
    ));
    return co;
}

Surface rayMarch(vec3 ro, vec3 rd, float start, float end){
    float depth = start;
    Surface co;

    for(int i = 0; i < MAX_MARCHING_STEPS; i++){
        vec3 p = ro + depth *rd;
        co = sdScene(p);
        depth += co.sd;
        if(co.sd < PRECISION || depth > end) break;
    }

    co.sd = depth;

    return co;
}

vec3 calcNormal(vec3 p){
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * sdScene(p + e.xyy).sd +
      e.yyx * sdScene(p + e.yyx).sd +
      e.yxy * sdScene(p + e.yxy).sd +
      e.xxx * sdScene(p + e.xxx).sd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 backgroundColor = vec3(0.835, 1.0, 1.0);

    vec3 col = vec3(0.0);
    vec3 ro = vec3(0.0, 0.0, 3.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    Surface co = rayMarch(ro, rd, MIN_DIST, MAX_DIST);

    if (co.sd > MAX_DIST){
        col = backgroundColor;
    } else {
        vec3 p = ro + rd * co.sd;
        vec3 normal = calcNormal(p);
        vec3 lightPosition = vec3(2.0, 2.0, 7.0);
        vec3 lightDirection = normalize(lightPosition - p);

        float dif = clamp(dot(normal, lightDirection), 0.3, 1.0);

        col = dif * co.col + backgroundColor * 0.2;
    }
    fragColor = vec4(col, 1.0);
}

float PI = 3.14159265;


float rand(float k, float seed){
    return sin(k*seed*seed);
}


mat3 rotMatrix(vec3 rotation){
    rotation *= PI/180.;
    float rx = rotation.x;
    float ry = rotation.y;
    float rz = rotation.z;

    mat3 rotationX = mat3( 1      , 0      , 0      ,
                           0      , cos(rx),-sin(rx),
                           0      , sin(rx), cos(rx));

    mat3 rotationY = mat3( cos(ry), 0      , sin(ry),
                          0       , 1      , 0      ,
                          -sin(ry), 0      , cos(ry));

    mat3 rotationZ = mat3( cos(rz),-sin(rz), 0      ,
                           sin(rz), cos(rz), 0      ,
                           0      , 0      , 1      );

    return rotationX * rotationY * rotationZ;
}


vec4 opSmoothUnion( vec4 d1, vec4 d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2.x-d1.x)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }


vec4 sphere(vec3 p, vec3 center, vec3 color, float s){
    vec4 valcolor = vec4(0, 0, 0, 0);

    float value = length(p - center)-s;

    valcolor.x = value;
    valcolor.yzw = color;

    return valcolor;
}


vec4 plane(vec3 p, vec3 normal, vec3 color, float h)
{
    vec4 valcolor = vec4(0, 0, 0, 0);

    float value = dot(p,normal) + h;

    valcolor.x = value;
    valcolor.yzw = color; 

    return valcolor;
}


vec4 sdf(in vec3 p){

    float random0 = rand(1000., float(int(iTime/2.)));
    float random1 = rand(2000., float(int(iTime/2.)));
    float h = 8. - 13.*pow((iTime/2. - float(int(iTime/2.))),2.);

    float random2 = rand(5000., float(int(iTime/2.+0.5)));
    float random3 = rand(7000., float(int(iTime/2.+0.5)));
    float h2 = 8. - 13.*pow((iTime/2.+0.5 - float(int(iTime/2.+0.5))),2.);

    return opSmoothUnion(sphere(p, vec3(2.*random0,2.*random1+2.,h), vec3(1,0,1), 1.),
           opSmoothUnion(sphere(p, vec3(2.*random2,2.*random3+2.,h2), vec3(0,1,1), 1.),
                         plane(p, vec3(0,0,1), vec3(1,1,1), 1.), 2.5), 2.5);
}
