bool sdfIntersect(out vec3 position, out vec3 normal, out vec3 color, in vec3 rayOri, in vec3 rayDir){
    int maxStep = 1000;
    int dichoSteps = 12;

    vec3 rayPosA = rayOri;
    vec3 rayPosB = rayOri;
    vec4 sdfValueA = sdf(rayOri)+0.01;
    vec4 sdfValueB = sdfValueA;

    // sphere-tracing
    int count = 0;
    while (sdfValueB.x > 0.){
        rayPosA = rayPosB; 
        rayPosB += (sdfValueB.x + 0.001) * rayDir;
        //rayPosB += 0.01 * rayDir;

        sdfValueA = sdfValueB;
        sdfValueB = sdf(rayPosB);

        if (count > maxStep || sdfValueB.x > 10.){
            return false;
        }

        count += 1;
    }

    // end with dichotomy
    vec3 rayPosAB = (rayPosA + rayPosB)/2.;
    vec4 sdfValueAB = sdf(rayPosAB);
    for (int i; i<dichoSteps; i++){
        if (sdfValueAB.x > 0.){
            rayPosA = rayPosAB;
            sdfValueA = sdfValueAB;
        } else {
            rayPosB = rayPosAB;
            sdfValueB = sdfValueAB;
        }

        rayPosAB = (rayPosA + rayPosB)/2.;
        sdfValueAB = sdf(rayPosAB);
    }

    position = rayPosAB;

    float epsilon = 0.0001;
    normal = vec3(sdf(position+vec3(epsilon, 0, 0)).x - sdf(position+vec3(-epsilon, 0, 0)).x,
                  sdf(position+vec3(0, epsilon, 0)).x - sdf(position+vec3(0, -epsilon, 0)).x,
                  sdf(position+vec3(0, 0, epsilon)).x - sdf(position+vec3(0, 0, -epsilon)).x);

    normal = normalize(normal);

    color = sdfValueB.yzw;

    return true;
}


float light(vec3 lightPos, vec3 objPos, vec3 normal){
    vec3 lightDir = normalize(lightPos - objPos);

    vec3 position2 = vec3(0,0,0);
    vec3 normal2 = vec3(0,0,0);
    vec3 color2 = vec3(0,0,0);

    vec3 rayOri = objPos;
    vec3 rayDir = lightDir;

    bool intersect = sdfIntersect(position2, normal2, color2, rayOri, rayDir);

    if (intersect && length(position2 - objPos)>0.0001){
        return 0.;
    }

    return max(0.,dot(lightDir, normal));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy - vec2(0.5, 0.5);

    float aspectRatio = iResolution.y/iResolution.x;

    vec2 sensorSize = vec2(16., 9.);//aspectRatio*16.);
    float focal = 10.;


    vec3 position = vec3(0,0,0);
    vec3 normal = vec3(0,0,0);
    vec3 color = vec3(0,0,0);

    vec3 rayOri = vec3(0, -5, 0);
    vec3 rayDir = normalize(vec3(sensorSize.x*uv.x, focal, sensorSize.y*uv.y));

    bool intersect = sdfIntersect(position, normal, color, rayOri, rayDir);

    vec3 col = vec3(uv.y/3.,uv.y/2.,uv.y);
    if (intersect){
        col = color * vec3(1,1,1) * (light(vec3(6,-1,8), position, normal));
        //col = (normal + vec3(1,1,1))/2.;
    }


    fragColor = vec4(col,1.0);
}

