float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 func(vec2 m) {
    vec2 r = m;
    r.x = 0.5 + 0.5 * thc(1000., 1. * iTime + 100. * m.x);
    r.y = 0.5 + 0.5 * ths(1000., 1. * iTime + 100. * m.y);
    return r;
}

vec2 func2(vec2 m) {
    vec2 r = m;
    r.x = thc(1.5, -0.04 * iTime * m.x + m.x * (1. + 0.2 * thc(4., iTime + m.y)));
    r.y = thc(1.5, -0.04 * iTime * m.y + m.y * (1. + 0.2 * thc(4., iTime + m.x)));
    return r;
}


vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float a = atan(uv.y, uv.x);
    float l = length(uv);

    float k = 0.15;// mix(0.5, 0.05, .5 + .5 * thc(6., 0.5 * iTime)) * max(abs(uv.y), abs(uv.x));

    vec2 m1 = .5 + .5 * func2(4. * k * uv);
    vec2 m2 = .5 + .5 * func2(8. * k *uv);
    vec2 m3 = .5 + .5 * func2(16. * k * uv);
    vec2 m4 = .5 + .5 * func2(32. * k * uv);
    vec2 m5 = .5 + .5 * func2(64. * k * uv);

    vec2 m = m1;
    m = mix(m, m2,  func(m2));
    m = mix(m, m3,  func(m3));
    m = mix(m, m4,  func(m4));
    m = mix(m, m5,  func(m5));



    uv = fract(m);

    //uv.x = ths(2., cos(12. * uv.x) + iTime);
    //uv.y = ths(2., sin(12. * uv.y) + iTime);

    // doesnt actually do much
    float sc = 1.;//1. + cos(4. * length(m));

    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float d = length(fpos);
    float p = 0.4 * cos(10. * a);
    float s = smoothstep(-p, p, 0.5 - d);
    s -= smoothstep(-p, p, 0.4 - d);
    s = clamp(s,0.,1.);


    s *= 12. * thc(1., 20. * max(abs(uv.x), abs(uv.y)) +  iTime);

    //vec3 col = vec3(s);

    vec3 e = vec3(.5);
