    float planeDist = p.y;

    //float box distance
    //position the box at -2, 2 above the ground, and 6 away from camera,
    //size of square is .3 in every direction
    float bd = dBox(p - vec3(-2, 2, 6), vec3(.3));
    //float torus distance
    float td = sdTorus(p - vec3(4, 2, 9), vec2(1., .5));
    float dis = min(planeDist, sphereDist);
    //actually adds the box to the shader
    dis = min(dis, bd);
    dis = min(dis, td);
    return dis;
}

// marching shoots a ray based on the camera position and direction of ray
float marching(vec3 camPos, vec3 rayDirec) {
    //keeping track of distance from origin
    float distOrig = 0.0;

    //marching loop
    for (int i=0; i<100; i++) {
        vec3 p = camPos + rayDirec * distOrig;
        //distance to scene
        float distScene = GetDist(p);
        distOrig += distScene;
        if(distOrig > max_dist || distScene < surf_hit) break;
    }
    //return the 
    return distOrig;
}


vec3 getNormal(vec3 p) {
    float dist = GetDist(p);
    vec2 e = vec2(.01, 0.);

    vec3 n = dist - vec3(
    //these are called "swizzles"; a trick to efficiently manipulate
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));

    return normalize(n);
}


//p is some point that we want to return light value based on
float returnLight(vec3 p) {
    //light position right above sphere
    vec3 lightPos = vec3(0, 5, 6);
    lightPos.xz += vec2(2.* sin(iTime), 2. * cos(iTime)) * 2.;
    vec3 normalizedLight = normalize(lightPos - p);
    vec3 n = getNormal(p);

    float diffuseLight = clamp(dot(n, normalizedLight), 0., 1.);
    float d = marching(p + n * surf_hit * 1.5, normalizedLight);
    if(d<length(lightPos-p)) diffuseLight *= .1;

    return diffuseLight;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalizing it so that the uv is 0 in the middle
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //camera at one above the ground

    vec3 camPos = vec3(0, 3, 0);
    vec3 rayDirec = normalize(vec3(uv.x,uv.y-.3, 1));

    //distance from ray marching function
    float dis = marching(camPos, rayDirec);

    vec3 p = camPos + rayDirec * dis;

    float diffuseLight = returnLight(p);
    col = vec3(diffuseLight);

    //the minimum distance is going to be at least one because
    //we are one unit above the ground
    //dis /= 6. ;

    //col = vec3(dis);
    //col = getNormal(p); //checks that the getNormal function is working
    //converting what I see to RGB confirms that the function is correctly done
    fragColor = vec4(col,1.0);
}

