#define PI 3.1415926535897932384626433832795
#define PI2 6.283185307179586476925286766559
#define PI4 12.566370614359172953850573533118

#define OCTAVES 6
#define SPEEDX (0.05)
#define SPEEDY (SPEEDX * .4)
#define RSCALE 2.4
#define GSCALE 2.8
#define BSCALE 2.1

vec2 random(vec2 uv){
    uv = vec2( dot(uv, vec2(123.7, 31.9) ),
               dot(uv, vec2(609.2, 183.71) ) );
    return -1.0 + 2.0 * fract( sin(uv) * 343803.23346);
}

float noise(vec2 uv, float scale) {
    uv *= scale;
    uv = abs(uv);
    vec2 index = floor(uv);
    vec2 fraction = fract(uv);
    vec2 blend = smoothstep(0.0, 1.0, fraction);
    return mix(
        mix(
            dot( random(index + vec2( 0.0, 0.0 ) ), fraction - vec2( 0.0, 0.0 ) ),
            dot( random(index + vec2( 1.0, 0.0 ) ), fraction - vec2( 1.0, 0.0 ) ),
            blend.x),
        mix(
            dot( random(index + vec2( 0.0, 1.0 ) ), fraction - vec2( 0.0, 1.0 ) ),
            dot( random(index + vec2( 1.0, 1.0 ) ), fraction - vec2( 1.0, 1.0 ) ),
            blend.x),
        blend.y) * 0.5 + 0.5;
}

float noiseX(vec2 tuv, vec2 uv, float scale) {
    // vec2 tuv = abs(uv);
    uv *= scale;
    uv = abs(uv);
    vec2 index = floor(uv);
    vec2 fraction = fract(uv);
    vec2 blend = smoothstep(0.0, 1.0, fraction);

    if (tuv.x > (iMouse.x / iResolution.x)-.5) {
        return mix(
            mix(
                dot( random(index + vec2( 0.0, 0.0 ) ), fraction - vec2( 1.0, 0.0 ) ),
                dot( random(index + vec2( 1.0, 0.0 ) ), fraction - vec2( 1.0, 0.0 ) ),
                blend.x),
            mix(
                dot( random(index + vec2( 0.0, 1.0 ) ), fraction - vec2( 0.0, 1.0 ) ),
                dot( random(index + vec2( 1.0, 1.0 ) ), fraction - vec2( 1.0, 1.0 ) ),
                blend.x),
            blend.y) * 0.5 + 0.5;
     } else {
        return mix(
            mix(
                dot( random(index + vec2( 0.0, 0.0 ) ), fraction - vec2( 0.0, 0.0 ) ),
                dot( random(index + vec2( 1.0, 0.0 ) ), fraction - vec2( 1.0, 0.0 ) ),
                blend.x),
            mix(
                dot( random(index + vec2( 0.0, 1.0 ) ), fraction - vec2( 0.0, 1.0 ) ),
                dot( random(index + vec2( 1.0, 1.0 ) ), fraction - vec2( 1.0, 1.0 ) ),
                blend.x),
            blend.y) * 0.5 + 0.5;
     }
}

float bnoise(vec2 c, float scale) {
    float t = 0.0;
    vec2 tc = c;
    c = abs(c);
    float octave = .7;
    float r0 = (12.0 + iTime * SPEEDX) * -sign(c.x);
    float r1 = (13.0 + iTime * SPEEDY) * -sign(c.y);
    float ot = noise(c + 12.0, 2.0);
    for( int a=0; a<OCTAVES; a++ ){
        c.x += r0;
        c.y += r1;
        float n = noiseX(tc, c + ot, scale) * octave;
        ot = n;
        octave /= 2.0;
        scale *= 2.0;
        t += n; // t = max(t, n);
    }
    return t;
}

float color(float t) {
    return pow(cos((1.0-t)*PI)*.5 + .5, 3.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    vec2 c = uv - .5;
    uv.x *= aspect;
    float r = bnoise(c, RSCALE);
    float g = bnoise(c, GSCALE);
    float b = bnoise(c, BSCALE);
    r = color(r);
    g = color(g);
    b = color(b);
    float rr = (r+g) / 2.0;
    float gg = (g+b) / 2.0;
    float bb = (b+r) / 2.0;
    fragColor = vec4(rr, gg, bb, 1.0);
}

#define TAU (6.283185307)
// gives pure saturated color from input [0, 6) for phase
vec3 hue(float x) {
    x = mod(x, 6.);
    return clamp(vec3(
        abs(x - 3.) - 1.,
        -abs(x - 2.) + 2.,
        -abs(x - 4.) + 2.
    ), 0., 1.);
}

vec3 colorize(float value, float hue_){
    vec3 a = hue(hue_);
    return mix(
      mix(
        vec3(0.), a , smoothstep(0., 0.5, value)
      ), vec3(1.), smoothstep(0.5, 1., value)
    );
}

float zigzag(float value){
    return abs(mod(value * 2., 2.) - 1.);
}

float dither(float value, float steps, float thres){
    float a = value * steps;
    return (floor(a) + step(thres, a - floor(a))) / steps;
}

float roll(vec2 xy_, uint t) {
    uvec2 xy = uvec2(xy_);
    const uint a = 1664525u;
    const uint m = 1013904223u;

    uint seed = 1818420u * xy.x + 78265178u * xy.y + 237698117u * t;

    uint x0 = (seed * a) % m;
    uint x = (x0 * a) % m;

    return float((x ^ x0) & 65535u) / 65536.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(iTime / 3.);
  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale;

  float dist = log(uv.x * uv.x + uv.y * uv.y);
  float angle = atan(uv.y, uv.x) / TAU;
  float dark = mod(floor(time * 50.), 2.);
  float thres = (
  mod(gl_FragCoord.x + gl_FragCoord.y, 2.) +
  roll(gl_FragCoord.xy, floatBitsToUint(time))
  )/2.;

  float spiral1 = dither(zigzag(
    angle + dist * 0.2 + (
    sin(time * TAU) * 1. - time * 4.
    ) * 3.
  ), 2., thres);

  float spiral2 = dither(
    -angle + dist * 0.2 + (
    sin(-time * TAU) * 1. - time * 4.
    ) * 3.
  , 12., thres);

  vec3 col = colorize(spiral1, spiral2 * 6.);
  // Output to screen
  fragColor = vec4(
    col, 1.0
  );
}

const vec3 one = vec3(55., 86., 32.)/255.;
const vec3 two = vec3(109., 142., 52.)/255.;
const vec3 three = vec3(129., 183., 12.)/255.;
const vec3 four = vec3(205., 219., 87.)/255.;

float random (vec2 _st) {
    return fract(sin(dot(_st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    vec2 st = fragCoord/iResolution.xy;
    vec2 uv = st;
    uv.x *= iResolution.x/iResolution.y;
    uv.x += iTime * .25;
    uv *= 10.;
    vec2 ipos = floor(uv);

    //put each square in a random group 1 <-> 4
    float randVal = random(ipos);
    float timeGroup;
    if (randVal > .75) timeGroup = 0.;
    else if (randVal > .5) timeGroup = 1.;
    else if (randVal > .25) timeGroup = 2.;
    else timeGroup = 3.;

    //randomize each group one at a time
    float timeVal = floor((iTime*12.+(1.*timeGroup))*.25);
    randVal = random(vec2(ipos.x + timeVal, ipos.y + timeVal));

    //assign color to square
    vec3 randColor;
    if (randVal > .75) randColor = one;
    else if (randVal > .5) randColor = two;
    else if (randVal > .25) randColor = three;
    else randColor = four;

    //add darkness on edges
    col = randColor * (1.-abs(st.x-.5)*1.5)*(1.-abs(st.y-.5)*1.5);

    fragColor = vec4(col,1.);
}

