// lpnorm by gaz
// https://www.shadertoy.com/view/slG3Rz
float lpnorm(vec2 p, float n){vec2 t=pow(abs(p),vec2(n));return pow(t.x+t.y,1./n);}

// iq's segment function
float sdSegment( in vec2 p, in vec2 a, in vec2 b ){
    vec2 ba = b-a;
    vec2 aba = abs(ba);
    float r = atan(aba.y,aba.x)/3.14;
    vec2 pa = p-a;
    //pa*=mat2(cos(r*.05+vec4(0,11,33,0)));
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return lpnorm( pa - ba*h, 6. );
}

