void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord/iResolution.xy;
    vec2 grid = vec2(.2,.5);
    vec2 ip = floor(p/grid);
    p = mod(p, grid)/grid*2.-1.;
    p.y*=iResolution.x/iResolution.y;
    p.y*=30.; // ???
    p.x -= mix(0., p.y*.01, clamp(sin(iTime),0.,1.));;
    //p.x*=mix(1., 2.+(1.-clamp(p.y+2., .0, 1.)*2.), clamp(sin(iTime),0.,1.));

	fontWeight = .45;//-clamp(sin(iTime*1.5)*2.,0.,1.)*.19;
    fontSize = .7;
    float num = ip.x+(1.-ip.y)*5.;// + floor(exp(clamp(sin(iTime*5.),0.,1.))*10.) + floor(sin(iTime*.5)*10.);
    float d = sdNumFont(p, fontSize, num);
    d = 1.-smoothstep(.0,.015,d);

    vec3 col = mix(vec3(0.102,0.102,0.102), vec3(.8, .77, .7), d);

    // Draw Skeleton
    col = max(col, vec3(.8, .77, .7)*.3*smoothstep(0.02, 0., abs(sdAllSeg(p/fontSize)+fontWeight)-.002));

	fragColor = vec4(col,1.0);
}

float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float box1 = sdOrientedBox(uv, vec2(-1, -1), vec2(2, 2), .1);
    float box2 = sdOrientedBox(uv, vec2(2, -1), vec2(-1, 2), .1);

    float x = min(box1, box2);

    float baseX = smoothstep(.00, .001, x);

    vec4 xColor = vec4(1., 0, 0, 1.) * ((sin(2. * iTime) + 1.)/2.);
    vec4 bgColor = vec4(.5, 0, 0, 1.) * ((sin(11. * iTime) + 1.)/2.);

    // Output to screen
    fragColor = mix(bgColor, xColor, baseX);
}

// Weierstrass transform (https://en.wikipedia.org/wiki/Weierstrass_transform)
// of piecewise linear function
// filter by summing S for each segment

// original was 864 characters, is now 825.
// but I guess I've made progress!

// save a few ch
#define R float
#define V vec2

// oneshade's error function approximation
R erf(R x)
{
    return sign(x) * sqrt(1. - exp2(-1.787776 * x * x));
}

// an integration.
// the Weierstrass transform?
// Gaussian filtered segment
R S(R x, V A, V B, R w) 
{
    const R k = sqrt(1. / acos(-1.));
    R h = B.x - A.x // one of two interleaved terms
    , m = k * w * (B.y - A.y) / h // variance?
    , v = mix(A.y, B.y, (x - A.x) / h) // where on segment vertically by linear interpolation?
    , a = (x - A.x) / w
    , b = (x - B.x) / w;
    return (
        (erf(a) - erf(b)) * v +
        (exp(-a * a) - exp(-b * b)) * m
        ) * .5;
}
// unsure have actually improved it, perhaps easier to read.
// Someone who is actually well trained in calculus
// shall have to take it from here.  My brain hurts!

R F(R x, R w)
{
    // example from Wikipedia
    return (S(x, V(-5, 1), V( 0, 1), w)
          + S(x, V( 0, 1), V( 5,-1), w)) * 4.25;
}
