void mainImage(out vec4 o, V q) 
{
    V r = iResolution.xy;
    R u = 10. / r.y;
    q = (q - .5 * r) * u;
    R w = mix(2.5005, 5., -cos(.5 * iTime)) //mix(.001, 5., .5 - .5 * cos(.5 * iTime)) // Filter width
    , y = F(q.x, w)
    , d = dFdx(y);
    o = vec4(sqrt(abs(q.y - y) / sqrt(u * u + d * d))); // antialias plot, gamma
}


