float cone(vec3 position, float radius1, float radius2, float halfHeight) {
    vec2 q = vec2(length(position.xz) - (radius2 + radius1) * 0.5, position.y);
    vec2 end = vec2((radius2 - radius1) * 0.5, halfHeight);
    vec2 segment = q - end * clamp(dot(q, end) / dot(end, end), -1.0, 1.0);
    float d = length(segment);
    if (segment.x > 0.0) {
        return d;
    }
    return max(-d, abs(q.y) - halfHeight);
}

